import React, { useEffect, useState } from 'react';

function Menu({ toogleOptions, onButtonClick }) {
    const [username, setUsername] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('token');
            if (!token) return;

            try {
                const response = await fetch('http://localhost:8080/api/user', {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                    },
                });

                if (response.ok) {
                    const data = await response.json();
                    setUsername(data.username); // Ustaw nazwę użytkownika
                } else {
                    console.error('Błąd podczas pobierania danych użytkownika:', response.statusText);
                }
            } catch (error) {
                console.error('Błąd sieci podczas pobierania danych użytkownika:', error);
            }
        };

        fetchUserData();
    }, []);

    return (
        <div>
            {username ? <p>Witaj, {username}!</p> : <p>Ładowanie danych...</p>}
        </div>
    );
}

export default Menu;
