import React, { useState, useEffect, useRef } from 'react';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const chatRef = useRef(null);

  // Lista dostƒôpnych emotikon√≥w
  const emojiList = [
    "üòä", "üòÇ", "üò¢", "üòç", "üòé", "üò±", "ü§î", "üòÖ", "ü•∫", "üò§", "‚ù§Ô∏è", "üî•"
  ];

  // Automatycznie przewijaj czat na d√≥≈Ç po ka≈ºdej nowej wiadomo≈õci
  useEffect(() => {
    chatRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const message = {
        id: Date.now(),
        text: newMessage,
        sender: 'me', // wiadomo≈õƒá od u≈ºytkownika
      };
      setMessages((prevMessages) => [...prevMessages, message]);
      setNewMessage('');
      simulateOtherUserResponse(); // Symulacja odpowiedzi innego u≈ºytkownika
    }
  };

  const simulateOtherUserResponse = () => {
    setIsTyping(true);
    setTimeout(() => {
      const message = {
        id: Date.now() + 1,
        text: generateRandomResponse(),
        sender: 'other', // odpowied≈∫ od innego u≈ºytkownika
      };
      setMessages((prevMessages) => [...prevMessages, message]);
      setIsTyping(false);
    }, 2000); // Op√≥≈∫nienie odpowiedzi od drugiego u≈ºytkownika
  };

  // Generowanie losowej odpowiedzi od innego u≈ºytkownika
  const generateRandomResponse = () => {
    const responses = [
      "Zgadzam siƒô z tobƒÖ!",
      "Ciekawe, co o tym my≈õlƒÖ inni!",
      "Muszƒô siƒô zastanowiƒá nad tym!",
      "To by≈Ça dobra decyzja!",
      "Nie jestem pewny, co o tym my≈õleƒá...",
      "Jestem w tym samym momencie!",
    ];
    return responses[Math.floor(Math.random() * responses.length)];
  };

  // Funkcja do dodawania emotikonu do wiadomo≈õci
  const addEmoji = (emoji) => {
    setNewMessage(newMessage + emoji);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <>
      <div className="flex-grow overflow-y-auto space-y-2 p-5">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`p-2 max-w-xs rounded-lg ${
              message.sender === 'me'
                ? 'ml-auto bg-blue-600 text-white'
                : 'mr-auto bg-gray-700 text-white'
            }`}
          >
            <span className="font-semibold">
              {message.sender === 'me' ? 'Ty' : 'Inny u≈ºytkownik'}:
            </span>{" "}
            {message.text}
          </div>
        ))}

        {isTyping && (
          <div className="p-2 max-w-xs bg-gray-600 text-white rounded-lg animate-pulse">
            Inny u≈ºytkownik pisze...
          </div>
        )}

        <div ref={chatRef} />
      </div>

      <div className="flex flex-col bg-gray-900 p-4 rounded-lg">
        {/* Dodawanie emotikon√≥w */}
        <div className="flex flex-wrap gap-2 mb-4">
          {emojiList.map((emoji, index) => (
            <button
              key={index}
              onClick={() => addEmoji(emoji)}
              className="text-2xl p-2 hover:bg-gray-700 rounded-lg"
            >
              {emoji}
            </button>
          ))}
        </div>

        {/* Pole wej≈õciowe i przycisk wysy≈Çania */}
        <div className="mt-4 flex">
          <input
            type="text"
            className="flex-grow p-2 rounded-l-lg bg-gray-800 text-white border border-gray-700 focus:outline-none"
            placeholder="Wpisz swojƒÖ wiadomo≈õƒá..."
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyDown={handleKeyPress}
          />
          <button
            onClick={handleSendMessage}
            className="bg-blue-600 text-white p-2 rounded-r-lg hover:bg-blue-700"
          >
            Wy≈õlij
          </button>
        </div>
      </div>
    </>
  );
}

export default Chat;
