import React, { useEffect, useState } from 'react';
import toast, { Toaster } from 'react-hot-toast';
import { useAuthStore } from  '../store/store';
import styles from '../styles/Username.module.css';
import { generateOTP, verifyOTP } from '../helper/helper';
import { useNavigate } from 'react-router-dom';

export default function Recovery() {

  const { username } = useAuthStore(state => state.auth);
  const [OTP, setOTP] = useState();
  const navigate = useNavigate();

  useEffect(() => {
    generateOTP(username).then((OTP) => {
      console.log(OTP);
      if (OTP) return toast.success('OTP has been sent to your email!');
      return toast.error('Problem while generating OTP!');
    });
  }, [username]);

  async function onSubmit(e) {
    e.preventDefault();
    try {
      let { status } = await verifyOTP({ username, code: OTP });
      if (status === 201) {
        toast.success('Verify Successfully!');
        return navigate('/reset');
      }  
    } catch (error) {
      return toast.error('Wrong OTP! Check email again!');
    }
  }

  // Handler for resending OTP
  function resendOTP() {
    let sentPromise = generateOTP(username);

    toast.promise(sentPromise, {
      loading: 'Sending...',
      success: <b>OTP has been sent to your email!</b>,
      error: <b>Could not send it!</b>,
    });

    sentPromise.then((OTP) => {
      console.log(OTP);
    });
  }

  return (
    <div className="container mx-auto flex justify-center items-center h-screen">
      <Toaster position='top-center' reverseOrder={false}></Toaster>

      <div className="flex flex-col items-center text-center w-full max-w-md">

        {/* Title: "Recovery" */}
        <div className="title mb-4">
          <h4 className='text-5xl font-bold text-white'>Recovery</h4>
        </div>



        {/* OTP Input Form */}
        <form className='w-full' onSubmit={onSubmit}>
          <div className="flex flex-col items-center gap-6">

            {/* Instruction Text */}
            <div className="input text-center">
              <span className='py-4 text-sm text-gray-500'>
                Enter 6 digit OTP sent to your email address.
              </span>

              {/* OTP Input */}
              <input 
                onChange={(e) => setOTP(e.target.value)} 
                className={styles.textbox} 
                type="text" 
                placeholder='OTP' 
              />
            </div>

            {/* Submit Button */}
            <button className={styles.btn} type='submit'>Recover</button>
          </div>
        </form>

        {/* Resend OTP Link */}
        <div className="text-center py-4">
          <span className='text-gray-500'>Nie przyszed≈Ç OTP? <button onClick={resendOTP} className='text-red-500'>Resend</button></span>
        </div>

      </div>
    </div>
  );
}
