import React, { useState, useEffect } from "react";

const Market = () => {
    const [activeTab, setActiveTab] = useState("market"); // Domy≈õlnie otwieramy Market
    const [tooltipVisible, setTooltipVisible] = useState(false);
    const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });
    const [tooltipContent, setTooltipContent] = useState(null);
    const [userItems, setUserItems] = useState([]); // Przedmioty u≈ºytkownika
    const [marketItems, setMarketItems] = useState([]); // Przedmioty na markecie
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });

    useEffect(() => {
        fetchMarketItems();
        fetchUserItems();
    }, []);

    /** üî• Poprawione pobieranie przedmiot√≥w z marketu */
    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            console.log("üîÑ Pobieram przedmioty z marketu...");
            const response = await fetch("http://localhost:8080/api/market", { // ‚úÖ POPRAWIONY ENDPOINT
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('‚ùå B≈ÇƒÖd pobierania przedmiot√≥w na markecie:', response.statusText);
                return;
            }

            const data = await response.json();
            console.log("‚úÖ Market items API response:", data);
            setMarketItems(data || []);
        } catch (error) {
            console.error('‚ùå B≈ÇƒÖd pobierania przedmiot√≥w na markecie:', error);
        }
    };

    /** üî• POPRAWIONE POBIERANIE EKWIPUNKU */
    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            console.log("üîÑ Pobieram przedmioty u≈ºytkownika...");
            const response = await fetch("http://localhost:8080/api/usersCreaturesAndItemsData", { // ‚úÖ POPRAWIONY ENDPOINT
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('‚ùå B≈ÇƒÖd pobierania przedmiot√≥w u≈ºytkownika:', response.statusText);
                return;
            }

            const data = await response.json();
            console.log("‚úÖ User items API response:", data);
            setUserItems(data?.userItems || []);
        } catch (error) {
            console.error('‚ùå B≈ÇƒÖd pobierania przedmiot√≥w u≈ºytkownika:', error);
        }
    };

    /** üî• Kupowanie przedmiotu */
    const handleBuy = async (itemId) => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            console.log("üõí Kupowanie przedmiotu ID:", itemId);
            const response = await fetch("http://localhost:8080/api/market/buy", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ marketItemId: itemId })
            });

            if (!response.ok) {
                console.error('‚ùå B≈ÇƒÖd kupowania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot zosta≈Ç kupiony!');
            fetchMarketItems(); // Od≈õwie≈º market
            fetchUserItems(); // Od≈õwie≈º ekwipunek
        } catch (error) {
            console.error('‚ùå B≈ÇƒÖd kupowania przedmiotu:', error);
        }
    };

    /** üî• Tooltipy */
    const handleMouseEnter = (event, item) => {
        setTooltipPosition({ x: event.pageX, y: event.pageY });
        setTooltipContent(item);
        setTooltipVisible(true);
    };

    const handleMouseMove = (event) => {
        setTooltipPosition({ x: event.pageX, y: event.pageY });
    };

    const handleMouseLeave = () => {
        setTooltipVisible(false);
    };

    return (
        <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">
            {/* Panel nawigacyjny */}
            <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg sticky top-0 z-10">
                <button onClick={() => setActiveTab("market")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Market
                </button>
                <button onClick={() => setActiveTab("inventory")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Inventory
                </button>
            </div>

            {/* Widok Marketu */}
            {activeTab === "market" && (
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 max-h-[60vh] overflow-y-auto">
                    {marketItems.length === 0 ? (
                        <p className="text-maincolor4 text-center">Brak przedmiot√≥w na markecie.</p>
                    ) : (
                        marketItems.map((item) => (
                            <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all"
                                onMouseEnter={(event) => handleMouseEnter(event, item.item)}
                                onMouseLeave={handleMouseLeave}
                                onMouseMove={handleMouseMove}>
                                <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.item?.name || "Brak nazwy"}</h3>
                                <img src={`images/${item.item?.photo || "default"}.png`} alt={item.item?.name || "Brak zdjƒôcia"} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                <p className="text-maincolor4 font-bold mt-2">Price: {item.buyoutPrice || "???"} coins</p>
                                <button onClick={() => handleBuy(item._id)} className="mt-3 bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all">
                                    Buy
                                </button>
                            </div>
                        ))
                    )}
                </div>
            )}

            {/* Tooltip */}
            {tooltipVisible && tooltipContent && (
                <div className="fixed top-0 left-0 p-2 bg-black text-white rounded-lg" style={{ top: tooltipPosition.y, left: tooltipPosition.x }}>
                    <h4>{tooltipContent.name}</h4>
                    <p>{tooltipContent.description}</p>
                </div>
            )}
        </div>
    );
};

export default Market;
