import React from "react";

const MarketItemCard = ({item}) => {
    const {item: itemDetails, type, buyoutPrice, startingPrice, endTime} = item;

    return(
        <div className="p-4 bg-gray-800 rounded shadow-sm">
            <img src={itemDetails.photo} alt={itemDetails.name} className="w-full h-32 object-cover"/>
            <h3 className="text-xl font-bold text-maincolor4">{item.item.name}</h3>
            <p>Typ: {type}</p>
            <p>Cena: {type ===  'fixed' ? `${buyoutPrice}` : `Start: ${startingPrice}`}</p>
            <p>Czas do ko≈Ñca: {new Date(endTime).toLocaleString()}</p>
            <button className="bg-blue-500 text-maincolor4 px-4 py-2 mt-2 rounded">
                {type === 'ficed' ? 'Kup teraz' : 'Licytuj'}
            </button>
        </div>
    );
};

export default MarketItemCard;
