import React, { useState, useEffect } from 'react';

function PrivateMessages({ username }) {
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState({ receiver: '', title: '', content: '' });
    const [selectedMessage, setSelectedMessage] = useState(null);
    const token = localStorage.getItem('token');
    const [showSendModal, setShowSendModal] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await fetch('http://localhost:8080/api/messages', {
                    method: 'GET',
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                if (response.ok) {
                    const data = await response.json();
                    setMessages(data);
                } else {
                    console.error('Błąd podczas pobierania wiadomości');
                }
            } catch (error) {
                console.error('Błąd serwera:', error);
            }
        };
        fetchMessages();
    }, [token]);

    const sendMessage = async () => {
        if (!newMessage.receiver || !newMessage.title || !newMessage.content) {
            setErrorMessage('Wszystkie pola są wymagane!');
            return;
        }

        if (newMessage.receiver === username) {
            setErrorMessage('Nie możesz wysłać wiadomości do samego siebie.');
            return;
        }

        try {
            const response = await fetch('http://localhost:8080/api/message', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify(newMessage),
            });

            if (response.ok) {
                alert('Wiadomość wysłana!');
                setNewMessage({ receiver: '', title: '', content: '' });
                setShowSendModal(false);
                setErrorMessage(''); // Reset błędu
            } else {
                const errorData = await response.json();
                setErrorMessage(errorData.error || 'Nie udało się wysłać wiadomości.');
            }
        } catch (error) {
            setErrorMessage('Błąd serwera. Spróbuj ponownie później.');
            console.error('Błąd serwera:', error);
        }
    };

    return (
        <div className="w-full h-screen bg-black flex flex-col p-5 justify-center text-maincolor4">
            <div className="w-full h-full bg-maincolor1 p-5 rounded-xl">
                <div className="flex justify-between items-center mb-5">
                    <h1 className="text-2xl font-bold">Wiadomości prywatne</h1>
                    <button
                        onClick={() => setShowSendModal(true)}
                        className="bg-maincolor1 text-maincolor4 border-maincolor2 border px-4 py-2 rounded shadow hover:bg-opacity-80"
                    >
                        Wyślij wiadomość
                    </button>
                </div>
                <div className="p-4 rounded-xl shadow-lg overflow-y-auto space-y-4 h-[70vh]">
                    {messages.length > 0 ? (
                        messages.map((msg) => (
                            <div
                                key={msg._id}
                                className={`p-4 rounded-lg shadow-md border ${
                                    msg.isRead ? 'border-maincolor5' : 'border-maincolor2'
                                }`}
                            >
                                <div className="flex justify-between items-center">
                                    <div>
                                        <p className="font-bold">{msg.title}</p>
                                        <p className="text-sm">Od: {msg.senderUsername}</p>
                                        <p className="text-xs">{new Date(msg.createdAt).toLocaleString()}</p>
                                    </div>
                                    <button
                                        onClick={() => deleteMessage(msg._id)}
                                        className="text-red-500 hover:text-red-700 text-sm"
                                    >
                                        Usuń
                                    </button>
                                </div>
                                <button
                                    onClick={() => {
                                        setSelectedMessage(msg);
                                        if (!msg.isRead) markMessageAsReaded(msg._id);
                                    }}
                                    className="mt-2 text-blue-500 hover:underline"
                                >
                                    {selectedMessage === msg ? 'Zwiń' : 'Zobacz szczegóły'}
                                </button>
                                {selectedMessage === msg && (
                                    <div className="mt-4">
                                        <p className="text-sm">{msg.content}</p>
                                    </div>
                                )}
                            </div>
                        ))
                    ) : (
                        <p className="text-center">Brak wiadomości</p>
                    )}
                </div>
            </div>

            {/* Modal do wysyłania wiadomości */}
            {showSendModal && (
                <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center">
                    <div className="bg-maincolor1 p-6 rounded-lg shadow-md w-1/2">
                        <h2 className="text-lg font-bold mb-4">Wyślij wiadomość</h2>
                        {errorMessage && <p className="text-red-500 mb-4">{errorMessage}</p>}
                        <input
                            type="text"
                            placeholder="Odbiorca"
                            value={newMessage.receiver}
                            onChange={(e) => setNewMessage({ ...newMessage, receiver: e.target.value })}
                            className="w-full mb-4 p-2 border rounded bg-black text-maincolor4"
                        />
                        <input
                            type="text"
                            placeholder="Tytuł"
                            value={newMessage.title}
                            onChange={(e) => setNewMessage({ ...newMessage, title: e.target.value })}
                            className="w-full mb-4 p-2 border rounded bg-black text-maincolor4"
                        />
                        <textarea
                            placeholder="Treść"
                            value={newMessage.content}
                            onChange={(e) => setNewMessage({ ...newMessage, content: e.target.value })}
                            className="w-full mb-4 p-2 border rounded bg-black text-maincolor4"
                        />
                        <div className="flex justify-end space-x-2">
                            <button
                                onClick={() => setShowSendModal(false)}
                                className="bg-red-500 text-white px-4 py-2 rounded shadow hover:bg-red-600"
                            >
                                Anuluj
                            </button>
                            <button
                                onClick={sendMessage}
                                className="bg-maincolor4 text-black px-4 py-2 rounded shadow hover:bg-opacity-80"
                            >
                                Wyślij
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default PrivateMessages;
