import React, { useEffect, useState } from "react";
function Misions({data}) {
  const [timeLeftTable, setTimeLeftTable] = useState([130,50,70,90,110,130]);
  const fullTimeTableuseState = [130,50,70,90,110,130];
  useEffect(() => {
    const timers = timeLeftTable.map((_, index) => {
      return setInterval(() => {
        setTimeLeftTable((prev) => {
          const newTimeLeft = [...prev];
          if (newTimeLeft[index] > 0) {
            newTimeLeft[index] -= 1; // Zmniejsz czas dla konkretnej misji
          }
          return newTimeLeft;
        });
      }, 1000);
    });

    // Wyczyść wszystkie timery po demontażu komponentu
    return () => timers.forEach((timer) => clearInterval(timer));
  }, [timeLeftTable]);

  // Formatowanie czasu w minutach i sekundach
  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };
    const placeForMissions = Array(6).fill("");
    return (
      <div className="absolute w-full bg-black1 h-screen  text-maincolor4">
        {placeForMissions.map((_,index) =>(
          <div className="w-[65vw] bg-black h-[15vh] m-[1.5vh]  rounded-3xl border-2 border-maincolor1 border-[1vh]">
              <div className="w-full h-full bg-maincolor1 border border-black rounded-3xl border-[1vh] flex items-center ">
                <div className=" ml-[1vh] w-[10vh] h-[10vh] rounded-full"><img src="images/water1-3.png" className="rounded-full"></img></div>
                <div className="bg-gradient-to-r from-maincolor2 to-maincolor5 w-3/5 h-2/5 rounded-full relative">
                <div className='h-full bg-maincolor1 absolute right-0 '  style={{ width: `${(timeLeftTable[index] / fullTimeTable[index]) * 100}%` }}></div>
                </div>
                <h1>Odliczanie: {formatTime(timeLeftTable[index])}</h1>
              </div>
          </div>
        ))}
      </div>
    );
  }
  
  export default Misions; // Eksport komponentu Content
