import React, { useState } from "react";

// Zestaw przedmiotów w sklepie
const itemsForSale = [
  { id: 1, name: "Miecz ognisty", price: 100, image: "/images/sword.png", description: "Potężny miecz, który zadaje obrażenia ogniste." },
  { id: 2, name: "Zbroja wulkaniczna", price: 200, image: "/images/armor.png", description: "Wysokiej jakości zbroja, odporna na ogień." },
  { id: 3, name: "Amulet smoka", price: 150, image: "/images/amulet.png", description: "Amulet, który zwiększa siłę ataku." },
  { id: 4, name: "Eliksir zdrowia", price: 50, image: "/images/potion.png", description: "Eliksir przywracający 50 punktów zdrowia." },
  { id: 5, name: "Tarcza Ognia", price: 120, image: "/images/shield.png", description: "Tarcza chroniąca przed ogniem i magią." },
  { id: 6, name: "Topór Wojenny", price: 180, image: "/images/axe.png", description: "Topór, który zadaje duże obrażenia w walce wręcz." },
  { id: 7, name: "Pierścień Mocy", price: 300, image: "/images/ring.png", description: "Pierścień, który zwiększa twoją moc bojową." },
  { id: 8, name: "Zbroja Lwa", price: 250, image: "/images/lionshield.png", description: "Zbroja, która zwiększa wytrzymałość i odporność." },

];

function Shop() {
  const [cart, setCart] = useState([]);
  const [inventory, setInventory] = useState([]);
  const [tradeItems, setTradeItems] = useState([]);
  const [activeTab, setActiveTab] = useState("shop"); // shop | inventory | trade

  // Dodaj przedmiot do koszyka
  const addToCart = (item) => {
    setCart([...cart, item]);
  };

  // Dodaj przedmiot do inventarza
  const addToInventory = (item) => {
    setInventory([...inventory, item]);
  };

  // Dodaj przedmiot do wymiany
  const addToTrade = (item) => {
    setTradeItems([...tradeItems, item]);
  };

  return (
    <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">
      {/* Panel nawigacyjny */}
      <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg">
        <button onClick={() => setActiveTab("shop")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">Sklep</button>
        <button onClick={() => setActiveTab("inventory")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">Inventarz</button>
        <button onClick={() => setActiveTab("trade")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">Wymiana</button>
      </div>

      {/* Sklep */}
      {activeTab === "shop" && (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {itemsForSale.map((item) => (
            <div key={item.id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all">
              <img src={item.image} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
              <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
              <p className="text-maincolor4 text-sm mb-2">{item.description}</p>
              <p className="text-maincolor4 font-bold mt-2">Cena: {item.price} złota</p>
              <button
                onClick={() => addToCart(item)}
                className="mt-3 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-all"
              >
                Dodaj do koszyka
              </button>
            </div>
          ))}
        </div>
      )}

      {/* Inventarz */}
      {activeTab === "inventory" && (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {inventory.length === 0 ? (
            <p className="text-maincolor4 text-center">Twój inventarz jest pusty!</p>
          ) : (
            inventory.map((item, index) => (
              <div key={index} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all">
                <img src={item.image} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                <p className="text-maincolor4 text-sm mb-2">{item.description}</p>
              </div>
            ))
          )}
        </div>
      )}

      {/* Wymiana */}
      {activeTab === "trade" && (
        <div className="flex flex-col space-y-4">
          <h2 className="text-2xl text-maincolor4 mb-4">Wymiana przedmiotów</h2>
          <div className="flex gap-4">
            <div className="w-1/2 bg-maincolor1 rounded-xl p-4 shadow-lg">
              <h3 className="text-xl text-maincolor4 mb-2">Twoje przedmioty</h3>
              {inventory.map((item, index) => (
                <div key={index} className="flex justify-between items-center bg-black1 p-2 rounded-lg mb-2">
                  <span className="text-maincolor4">{item.name}</span>
                  <button
                    onClick={() => addToTrade(item)}
                    className="bg-green-600 text-white py-1 px-4 rounded-lg hover:bg-green-700 transition-all"
                  >
                    Dodaj do wymiany
                  </button>
                </div>
              ))}
            </div>

            <div className="w-1/2 bg-maincolor1 rounded-xl p-4 shadow-lg">
              <h3 className="text-xl text-maincolor4 mb-2">Przedmioty do wymiany</h3>
              {tradeItems.map((item, index) => (
                <div key={index} className="bg-black1 p-2 rounded-lg mb-2">
                  <span className="text-maincolor4">{item.name}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Koszyk */}
      {activeTab === "shop" && cart.length > 0 && (
        <div className="mt-6 p-4 bg-maincolor1 rounded-xl shadow-xl">
          <h3 className="text-xl text-maincolor4 mb-2">Koszyk</h3>
          <ul>
            {cart.map((item, index) => (
              <li key={index} className="flex justify-between mb-2 p-2 border-b border-maincolor4">
                <span className="text-maincolor4">{item.name}</span>
                <span className="text-maincolor4">{item.price} złota</span>
              </li>
            ))}
          </ul>
          <button className="mt-4 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-all">
            Zakup ({cart.reduce((total, item) => total + item.price, 0)} złota)
          </button>
        </div>
      )}
    </div>
  );
}

export default Shop;
