import React, {useState} from "react";

function MarketAddItem(){
    const [itemId, setItemId] = useState('');
    const [type, setType] = useState('fixed');
    const [price, setPrice] = useState('');
    const [duration, setDuration] = useState(2);
    const [startingPrice, setStartingPrice] = useState('');

    const handleSubmit = async (e) => {
        e.prevendDefault();
        const token = localStorage.getItem('token');

        try{
            const response = await fetch('http://localhost:8080/api/market/add', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({itemId, type, price, duration, startingPrice}),
            });

            if(response.ok){
                alert('Przedmiot dodany na rynek!');
            }else{
                alert('Błąd podczas dodawania przedmiotu.');
            }
        }catch(error){
            console.error('Błąd: ',error);
        }
    };

    return(
        <form onSubmit={handleSubmit} className="mb-6 p-4 bg-gray-800 rounded">
            <h2 className="text-xl font-bold text-maincolor4 mb-4">Dodaj przedmiot na rynek</h2>

            <label className="block text-maincolor4 mb-2">ID Przedmiotu</label>
      <input
        type="text"
        value={itemId}
        onChange={(e) => setItemId(e.target.value)}
        className="w-full p-2 mb-4 rounded bg-gray-900 text-maincolor4"
        required
      />

      <label className="block text-maincolor4 mb-2">Typ sprzedaży</label>
      <select
        value={type}
        onChange={(e) => setType(e.target.value)}
        className="w-full p-2 mb-4 rounded bg-gray-900 text-maincolor4"
      >
        <option value="fixed">Stała cena</option>
        <option value="auction">Licytacja</option>
      </select>

      {type === 'fixed' ? (
        <>
          <label className="block text-maincolor4 mb-2">Cena</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            className="w-full p-2 mb-4 rounded bg-gray-900 text-maincolor4"
            required
          />
        </>
      ) : (
        <>
          <label className="block text-maincolor4 mb-2">Cena początkowa</label>
          <input
            type="number"
            value={startingPrice}
            onChange={(e) => setStartingPrice(e.target.value)}
            className="w-full p-2 mb-4 rounded bg-gray-900 text-maincolor4"
            required
          />
        </>
      )}

      <label className="block text-maincolor4 mb-2">Czas trwania (godziny)</label>
      <select
        value={duration}
        onChange={(e) => setDuration(e.target.value)}
        className="w-full p-2 mb-4 rounded bg-gray-900 text-maincolor4"
      >
        <option value={2}>2 godziny</option>
        <option value={8}>8 godzin</option>
        <option value={24}>24 godziny</option>
      </select>

      <button type="submit" className="w-full p-2 bg-blue-600 text-white rounded hover:bg-blue-700">
        Dodaj na rynek
      </button>
    </form>
  );
}

export default MarketAddItem;
