import React, { useState, useEffect } from "react";
import Modal from "react-modal";

const Market = () => {
    const [activeTab, setActiveTab] = useState("market"); // Zakładki: "market" lub "inventory"
    const [marketItems, setMarketItems] = useState([]);
    const [userItems, setUserItems] = useState([]); // Przedmioty użytkownika
    const [tooltipVisible, setTooltipVisible] = useState(false);
    const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });
    const [tooltipContent, setTooltipContent] = useState(null);
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });

    useEffect(() => {
        fetchMarketItems();
        fetchUserItems();
    }, []);

    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/items", {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów:', response.statusText);
                return;
            }

            const data = await response.json();
            setMarketItems(data);
        } catch (error) {
            console.error('Błąd pobierania przedmiotów:', error);
        }
    };

    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/user/items", {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów użytkownika:', response.statusText);
                return;
            }

            const data = await response.json();
            setUserItems(data);
        } catch (error) {
            console.error('Błąd pobierania przedmiotów użytkownika:', error);
        }
    };

    const handleSell = async (type) => {
        const token = localStorage.getItem('token');
        if (!token || !selectedItem) {
            console.warn('Brak tokenu lub nie wybrano przedmiotu');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/sell", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    ...sellData,
                    itemId: selectedItem._id,
                    type
                })
            });

            if (!response.ok) {
                console.error('Błąd wystawiania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został wystawiony!');
            fetchMarketItems();
            setIsSellModalOpen(false);
        } catch (error) {
            console.error('Błąd wystawiania przedmiotu:', error);
        }
    };

    const handleMouseEnter = (event, item) => {
        setTooltipPosition({ x: event.pageX, y: event.pageY });
        setTooltipContent(item);
        setTooltipVisible(true);
    };

    const handleMouseLeave = () => {
        setTooltipVisible(false);
    };

    return (
        <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">
            <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg sticky top-0 z-10">
                <button onClick={() => setActiveTab("market")} className={`text-maincolor4 text-lg hover:text-blue-600 transition-all ${activeTab === 'market' ? 'font-bold' : ''}`}>
                    Market
                </button>
                <button onClick={() => setActiveTab("inventory")} className={`text-maincolor4 text-lg hover:text-blue-600 transition-all ${activeTab === 'inventory' ? 'font-bold' : ''}`}>
                    Inventory
                </button>
            </div>

            {activeTab === "market" && (
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                    {marketItems.map((item) => (
                        <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all"
                            onMouseEnter={(event) => handleMouseEnter(event, item)}
                            onMouseLeave={handleMouseLeave}>
                            <img src={`images/${item.photo}.png`} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                            <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                            <p className="text-maincolor4 text-sm mb-2">Price: {item.price}</p>
                        </div>
                    ))}
                </div>
            )}

            {activeTab === "inventory" && (
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                    {userItems.map((item) => (
                        <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all"
                            onMouseEnter={(event) => handleMouseEnter(event, item)}
                            onMouseLeave={handleMouseLeave}>
                            <img src={`images/${item.photo}.png`} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                            <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                            <button
                                onClick={() => { setSelectedItem(item); setIsSellModalOpen(true); }}
                                className="mt-3 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all"
                            >
                                Sell / Auction
                            </button>
                        </div>
                    ))}
                </div>
            )}

            <Modal
                isOpen={isSellModalOpen}
                onRequestClose={() => setIsSellModalOpen(false)}
                className="bg-maincolor2 p-8 rounded-lg shadow-xl max-w-lg mx-auto text-maincolor4"
                overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center"
            >
                <h2 className="text-2xl font-bold mb-6">Sprzedaj lub Licytuj</h2>
                {selectedItem && (
                    <div>
                        <h3 className="font-bold mb-4">{selectedItem.name}</h3>
                        <label className="block mb-2">Cena</label>
                        <input
                            type="number"
                            value={sellData.price}
                            onChange={(e) => setSellData({ ...sellData, price: e.target.value })}
                            className="w-full p-2 border rounded mb-4"
                        />
                        <label className="block mb-2">Czas trwania (godziny)</label>
                        <select
                            value={sellData.duration}
                            onChange={(e) => setSellData({ ...sellData, duration: e.target.value })}
                            className="w-full p-2 border rounded mb-4"
                        >
                            <option value="2">2 godziny</option>
                            <option value="8">8 godzin</option>
                            <option value="24">24 godziny</option>
                        </select>
                        <button
                            onClick={() => handleSell('fixed')}
                            className="bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 w-full mb-2"
                        >
                            Sprzedaj
                        </button>
                        <button
                            onClick={() => handleSell('auction')}
                            className="bg-green-600 text-white py-2 px-4 rounded hover:bg-green-700 w-full"
                        >
                            Licytuj
                        </button>
                    </div>
                )}
            </Modal>

            {tooltipVisible && tooltipContent && (
                <div
                    style={{
                        position: "fixed",
                        top: tooltipPosition.y + 10,
                        left: tooltipPosition.x + 10,
                        zIndex: 10,
                        backgroundColor: "rgba(0, 0, 0, 0.8)",
                        color: "white",
                        padding: "8px",
                        borderRadius: "10px",
                    }}
                    className="tooltip"
                >
                    <h4 className="text-xl font-bold">{tooltipContent.name}</h4>
                    <p>Price: {tooltipContent.price}</p>
                </div>
            )}
        </div>
    );
};

export default Market;
