import React, { useState, useEffect } from "react";

const Market = () => {
    const [activeTab, setActiveTab] = useState("market"); // Domyślnie otwieramy Market
    const [tooltipVisible, setTooltipVisible] = useState(false);
    const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });
    const [tooltipContent, setTooltipContent] = useState(null);
    const [userItems, setUserItems] = useState([]); // Przedmioty użytkownika
    const [marketItems, setMarketItems] = useState([]); // Przedmioty na markecie
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ price: "", duration: "2" });

    useEffect(() => {
        fetchMarketItems();
        fetchUserItems();
    }, []);

    /** 🔥 Pobieranie przedmiotów z marketu */
    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów na markecie:', response.statusText);
                return;
            }

            const data = await response.json();
            console.log("✅ Market items:", data);
            setMarketItems(data);
        } catch (error) {
            console.error('❌ Błąd pobierania przedmiotów na markecie:', error);
        }
    };

    /** 🔥 Pobieranie ekwipunku gracza */
    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/usersCreaturesAndItemsData", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów użytkownika:', response.statusText);
                return;
            }

            const data = await response.json();
            console.log("✅ User items:", data?.userItems);
            setUserItems(data?.userItems || []);
        } catch (error) {
            console.error('❌ Błąd pobierania przedmiotów użytkownika:', error);
        }
    };

    /** 🔥 Kupowanie przedmiotu */
    const handleBuy = async (itemId) => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/buy", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ marketItemId: itemId })
            });

            if (!response.ok) {
                console.error('Błąd kupowania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został kupiony!');
            fetchMarketItems(); // Odśwież market
            fetchUserItems(); // Odśwież ekwipunek
        } catch (error) {
            console.error('❌ Błąd kupowania przedmiotu:', error);
        }
    };

    /** 🔥 Obsługa sprzedaży */
    const handleSell = async () => {
        const token = localStorage.getItem('token');
        if (!token || !selectedItem) {
            console.warn('Brak tokenu lub nie wybrano przedmiotu');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/sell", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    ...sellData,
                    itemId: selectedItem._id
                })
            });

            if (!response.ok) {
                console.error('Błąd wystawiania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został wystawiony!');
            fetchMarketItems(); // Odśwież market
            fetchUserItems(); // Odśwież ekwipunek
            setIsSellModalOpen(false);
        } catch (error) {
            console.error('❌ Błąd wystawiania przedmiotu:', error);
        }
    };

    /** 🔥 Tooltipy */
    const handleMouseEnter = (event, item) => {
        setTooltipPosition({ x: event.pageX, y: event.pageY });
        setTooltipContent(item);
        setTooltipVisible(true);
    };

    const handleMouseMove = (event) => {
        setTooltipPosition({ x: event.pageX, y: event.pageY });
    };

    const handleMouseLeave = () => {
        setTooltipVisible(false);
    };

    return (
        <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">
            {/* Panel nawigacyjny */}
            <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg sticky top-0 z-10">
                <button onClick={() => setActiveTab("market")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Market
                </button>
                <button onClick={() => setActiveTab("inventory")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Inventory
                </button>
            </div>

            {/* Widok Marketu */}
            {activeTab === "market" && (
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 max-h-[60vh] overflow-y-auto">
                    {marketItems.length === 0 ? (
                        <p className="text-maincolor4 text-center">Brak przedmiotów na rynku.</p>
                    ) : (
                        marketItems.map((item) => (
                            <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all"
                                onMouseEnter={(event) => handleMouseEnter(event, item.item)}
                                onMouseLeave={handleMouseLeave}
                                onMouseMove={handleMouseMove}>
                                <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.item.name}</h3>
                                <img src={`images/${item.item.photo}.png`} alt={item.item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                <p className="text-maincolor4 font-bold mt-2">Price: {item.buyoutPrice || item.startingPrice} coins</p>
                                <button onClick={() => handleBuy(item._id)} className="mt-3 bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all">
                                    Buy
                                </button>
                            </div>
                        ))
                    )}
                </div>
            )}

            {/* Tooltip */}
            {tooltipVisible && tooltipContent && (
                <div className="fixed top-0 left-0 p-2 bg-black text-white rounded-lg" style={{ top: tooltipPosition.y, left: tooltipPosition.x }}>
                    <h4>{tooltipContent.name}</h4>
                    <p>{tooltipContent.description}</p>
                </div>
            )}
        </div>
    );
};

export default Market;
