import React, { useState, useEffect } from 'react';

function PrivateMessages() {
    const [messages, setMessages] = useState([]); //Przechowywanie wiadomości użytkownika
    const [newMessage, setNewMessage] = useState({receiver: '', title: '', content: ''});//Nowa wiadomość
    const [selectedMessage, setSelectedMessage] = useState(null); //wybrana wiadomość
    const token = localStorage.getItem('token');

    //Pobieranie wiadomości po załadowaniu komponentu
    useEffect(() => {
        const fetchMessages = async () => {
            try{
                const response = await fetch('http://localhost:8080/api/messages', {
                    method: 'GET',
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                if (response.ok){
                    const data = await response.json();
                    console.log('Pobrane wiadomości: ',data);
                    setMessages(data);//Ustawienie wiadomości
                }else{
                    console.error('Błąd podczas pobierania wiadomości');
                }
            } catch(error){
                console.error('Błąd serwera:',error);
            }
        };
        fetchMessages();
    }, [token]);

    //Wysyłąnie nowej wiadomości
    const sendMessage = async () => {
        if(!newMessage.receiver || !newMessage.title || !newMessage.content){
            alert('Wszystkie pola są wymagane!');
            return;
        }
        try{
            const response = await fetch('http://localhost:8080/api/message', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify(newMessage),
            });
            if(response.ok){
                alert('Wiadomość wysłana!');
                setNewMessage({receiver: '', title: '', content: ''}); //reset formularza
            } else {
                console.error('Błąd podczas wysyłania wiadomości');
            }
        } catch (error) {
            console.error('Błąd serwera:', error);
        }
    };

    //Usuwanie wiadomości
    const deleteMessage = async (messageId) => {
        try{
            const response = await fetch(`http://localhost:8080/api/messages/${messageId}`, {
                method: 'DELETE',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            if (response.ok) {
                setMessages((prevMessages) => prevMessages.filter((msg) => msg._id !== messageId));
            } else {
                console.error('Błąd podczas usuwania wiadomości');
            }
        } catch (error){
            console.error('Błąd serwera:', error);
        }
    };

    return (
        <div className="w-full h-screen bg-black1 flex flex-col p-6 text-maincolor4">
          {/* Nagłówek */}
          <div className="flex justify-between items-center mb-4">
            <h1 className="text-2xl font-bold">Wiadomości prywatne</h1>
            <button
              onClick={() => setShowSendModal(true)}
              className="bg-maincolor4 text-black1 px-4 py-2 rounded shadow hover:bg-opacity-80"
            >
              Wyślij wiadomość
            </button>
          </div>
    
          {/* Lista wiadomości */}
          <div className="flex-grow bg-maincolor1 rounded-lg p-4 overflow-y-auto space-y-4">
            {messages.length > 0 ? (
              messages.map((msg) => (
                <div
                  key={msg._id}
                  className={`p-4 rounded-lg shadow-md border ${
                    msg.isRead ? "bg-black1" : "bg-maincolor4"
                  }`}
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <p className="font-bold">{msg.title}</p>
                      <p className="text-sm">Od: {msg.senderUsername}</p>
                      <p className="text-xs">Wysłano: {new Date(msg.createdAt).toLocaleString()}</p>
                    </div>
                    <button
                      onClick={() => deleteMessage(msg._id)}
                      className="text-red-500 hover:text-red-700 text-sm"
                    >
                      Usuń
                    </button>
                  </div>
                  <button
                    onClick={() => {
                      setSelectedMessage(msg);
                      if (!msg.isRead) markAsRead(msg._id);
                    }}
                    className="mt-2 text-blue-500 hover:underline"
                  >
                    {selectedMessage === msg ? "Zwiń" : "Zobacz szczegóły"}
                  </button>
                  {selectedMessage === msg && (
                    <div className="mt-4">
                      <p className="text-sm">{msg.content}</p>
                    </div>
                  )}
                </div>
              ))
            ) : (
              <p>Brak wiadomości</p>
            )}
          </div>
    
          {/* Modal do wysyłania wiadomości */}
          {showSendModal && (
            <div className="fixed inset-0 bg-black1 bg-opacity-75 flex justify-center items-center">
              <div className="bg-maincolor1 p-6 rounded-lg shadow-md w-1/2">
                <h2 className="text-lg font-bold mb-4">Wyślij wiadomość</h2>
                <input
                  type="text"
                  placeholder="Odbiorca"
                  value={newMessage.receiver}
                  onChange={(e) => setNewMessage({ ...newMessage, receiver: e.target.value })}
                  className="w-full mb-4 p-2 border rounded bg-black1 text-maincolor4"
                />
                <input
                  type="text"
                  placeholder="Tytuł"
                  value={newMessage.title}
                  onChange={(e) => setNewMessage({ ...newMessage, title: e.target.value })}
                  className="w-full mb-4 p-2 border rounded bg-black1 text-maincolor4"
                />
                <textarea
                  placeholder="Treść"
                  value={newMessage.content}
                  onChange={(e) => setNewMessage({ ...newMessage, content: e.target.value })}
                  className="w-full mb-4 p-2 border rounded bg-black1 text-maincolor4"
                />
                <div className="flex justify-end space-x-2">
                  <button
                    onClick={() => setShowSendModal(false)}
                    className="bg-red-500 text-white px-4 py-2 rounded shadow hover:bg-red-600"
                  >
                    Anuluj
                  </button>
                  <button
                    onClick={sendMessage}
                    className="bg-maincolor4 text-black1 px-4 py-2 rounded shadow hover:bg-opacity-80"
                  >
                    Wyślij
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      );
    }
    
    export default PrivateMessages;
