import React, { useState, useEffect } from "react";
//import Modal from "react-modal";

const Market = () => {
    const [activeTab, setActiveTab] = useState("shop"); // Stan kontrolujcy aktywn zakadk ("shop" lub "inventory")
    const [shopItems, setShopItems] = useState([]);
    const [tooltipVisible, setTooltipVisible] = useState(false);
    const [tooltipPosition, setTooltipPosition] = useState({x:0, y: 0});
    const [tooltipContent, setTooltipContent] = useState(null);
    const [userItems, setUserItems] = useState([]);
    const [marketItems, setMarketItems] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });
    
    /**do zamiany */
    const ShopData = async (newShopForMoney = false) =>{
        const token = localStorage.getItem('token');
        if(!token){
            console.warn('Brak tokenu w localStorage');
            return;
        }
        try{
            const response = await fetch(`http://localhost:8080/api/ItemShop?newShopForMoney=${newShopForMoney}`,{
            method: 'GET',
            headers:{
                'Authorization': `Bearer ${token}`,
            },
        });
        if (!response.ok) {
            console.error('Bd pobierania sklepu:', response.statusText);
            return;
        }
        const data = await response.json();
        console.log(data);
        setShopItems(data.ShopItems);
        setUserItems(data.userItems);
        console.log(shopItems);
        }catch(error){
            console.error('Bd pobierania sklepu:');
            return;
        }
        }
      // Funkcja dodajca przedmioty do inventarza
    
    useEffect(()=>{
        const fetchShopData = async () => {
        await ShopData();  
        } 
        fetchShopData();
    },[]);
    
    const isSellModalOpenClick = async(item) => {
        setSelectedItem(item);
        await setIsSellModalOpen(true);
        
    }
    
    const handleMouseEnter = (actualEvent, item) =>{
        const rect = actualEvent.currentTarget.getBoundingClientRect();
        setTooltipPosition({ x: actualEvent.pageX, y: actualEvent.pageY });
        setTooltipContent(item);
        setTooltipVisible(true);
    };
    
    const handleMouseMove = (event) => {
        setTooltipPosition({ x: event.pageX, y: event.pageY }); // Aktualizuj pozycj tooltipa podczas ruchu myszy
    };
    const handleMouseLeave = () => {
        setTooltipVisible(false);
    }

    useEffect(() => {
        fetchMarketItems();
        fetchUserItems();
    }, []);

    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market", {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                console.error('Bd pobierania przedmiot贸w:', response.statusText);
                return;
            }

            const data = await response.json();
            console.log(" Market response:", data);

            setMarketItems(data || []);
        } catch (error) {
            console.error('Bd pobierania przedmiot贸w:', error);
        }
    };

    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }
        try {
            const response = await fetch("http://localhost:8080/api/user/usersCreaturesAndItemsDate", {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                console.error('Bd pobierania przedmiot贸w u偶ytkownika:', response.statusText);
                return;
            }

            const data = await response.json();
            setUserItems(data?.userItems || []);
        } catch (error) {
            console.error('Bd pobierania przedmiot贸w u偶ytkownika:', error);
        }
    };

    const handleSell = async (type) => {
        const token = localStorage.getItem('token');
        if (!token || !selectedItem) {
            console.warn('Brak tokenu lub nie wybrano przedmiotu');
            return;
        }
        try {
            const response = await fetch("http://localhost:8080/api/market/sell", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    ...sellData,
                    itemId: selectedItem._id,
                    type
                })
            });
            if (!response.ok) {
                console.error('Bd wystawiania przedmiotu:', response.statusText);
                return;
            }
            alert('Przedmiot zosta wystawiony!');
            fetchMarketItems();
            setIsSellModalOpen(false);
        } catch (error) {
            console.error('Bd wystawiania przedmiotu:', error);
        }
    };

    
    return (
    
        <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">      
            {shopItems ?(<>
            {/* Panel nawigacyjny */}  
            <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg sticky top-0 z-10">
            {/* Przycisk przeczajcy na widok marketu */}
                <button onClick={() => setActiveTab("market")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Market
                </button>
            {/* Przycisk przeczajcy na widok inventarza */}
                <button onClick={() => setActiveTab("inventory")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Inventory
                </button>
            </div>
    
            {/* Widok marketu */}
            {activeTab === "market" && (
            <div className="flex-grow overflow-y-auto">
                {/* Lista przedmiot贸w na markecie */}
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 max-h-[60vh]">
                    {marketItems.length === 0 ? (
                        <p className="text-maincolor4 text-center">Brak przedmiot贸w na markecie.</p>
                    ) : (
                        marketItems.map((item) => (
                            <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all"
                                onMouseEnter={(event) => handleMouseEnter(event, item.item)}
                                onMouseLeave={handleMouseLeave}
                                onMouseMove={handleMouseMove}>
                                <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.item?.name || "Brak nazwy"}</h3>
                                <img src={`images/${item.item?.photo || "default"}.png`} 
                                    alt={item.item?.name || "Brak zdjcia"} 
                                    className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                <p className="text-maincolor4 font-bold mt-2">Cena: {item.buyoutPrice || "???"} coins</p>
                                <button onClick={() => handleBuy(item._id)} className="mt-3 bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all">
                                    Kup
                                </button>
                            </div>
                        ))
                    )}
                </div>
                {tooltipVisible && (
                <div
                    style={{
                        position: "fixed",
                        top: tooltipPosition.y+10,
                        left: tooltipPosition.x+10,
                        zIndex: 10,
                        backgroundColor: "rgba(0, 0, 0, 0.8)",
                        color: "white",
                        padding: "8px",
                        borderRadius: "20px",
                    
                    }}
                    className={`border-4  ${tooltipContent?.element === "fire"
                    ? "border-red-500"
                    : tooltipContent?.element === "water"
                    ? "border-cyan-500"
                    : tooltipContent?.element === "nature"
                    ? "border-green-400"
                    : tooltipContent?.element === "light"
                    ? "border-yellow-500"
                    : tooltipContent?.element === "dark"
                    ? "border-purple-900"
                    : "border-white"} `}
                >
                        <h4 className="text-2xl font-bold">{tooltipContent?.name}</h4>
                        <img className="w-20 h-20" src={`images/${tooltipContent?.photo}.png`}></img>
                        <p className="text-lg font-bold">TYPE: {tooltipContent?.type} </p>
                        {tooltipContent?.power !== 0  &&(
                        <p>POWER: +{tooltipContent?.power}</p>)}
                        {tooltipContent?.vitality !== 0  &&(
                        <p>VITALITY: +{tooltipContent?.vitality}</p>)}
                        {tooltipContent?.strength !== 0  &&(
                        <p>STRENGTH: +{tooltipContent?.strength}</p>)}
                        {tooltipContent?.dexterity !== 0  &&(
                        <p>DEXTERITY: +{tooltipContent?.dexterity}</p>)}
                        {tooltipContent?.intelligence !== 0  &&(
                        <p>INTELLIGENCE: +{tooltipContent?.intelligence}</p>)}
                        {tooltipContent?.armor !== 0  &&(
                        <p>ARMOR: +{tooltipContent?.armor}</p>)}
                        <p><span>ELEMENT: </span>{" "}<span className={` ${
                    tooltipContent?.element === "fire"
                    ? "text-red-500"
                    : tooltipContent?.element === "water"
                    ? "text-cyan-500"
                    : tooltipContent?.element === "nature"
                    ? "text-green-400"
                    : tooltipContent?.element === "light"
                    ? "text-yellow-500"
                    : tooltipContent?.element === "dark"
                    ? "text-purple-900"
                    : "text-white"} font-bold`}>{tooltipContent?.element}</span></p>
                </div>
                )}
            </div>
            )}
    
            {/* Widok inventarza */}
            {activeTab === "inventory" && (
                <div className="flex-grow overflow-y-auto ">
                {/* Lista przedmiot贸w w inventarzu */}
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {userItems.length === 0 ? (
                        // Wywietla komunikat, gdy inventarz jest pusty
                        <p className="text-maincolor4 text-center">Tw贸j inventarz jest pusty!</p>
                        ) : (
                        // Wywietla przedmioty w inventarzu
                            userItems.map((item, index) => (
                            <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all"
                            onMouseEnter={(event) => handleMouseEnter(event, item)} onMouseLeave={handleMouseLeave} onMouseMove={handleMouseMove}>
                                {/* Obraz przedmiotu */}
                                <img src={`images/${item.photo}.png`} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                {/* Nazwa przedmiotu */}
                                <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                                {/* Opis przedmiotu */}
                                <p className="text-maincolor4 text-sm mb-2">{item.description}</p>
                                <p className="text-maincolor4 font-bold mt-2">Sell value: {Math.round(item.price / 3)} coins</p>
                                {/* Przycisk dodania do inventarza */}
                        
                                <button
                                    onClick={()=>isSellModalOpenClick(item)}
                                    
                                    className="mt-3 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all"
                                >
                                    Sell item
                                </button>
                            </div>
                            ))
                        )}
                    </div>
                    {tooltipVisible && (
                    <div
                        style={{
                            position: "fixed",
                            top: tooltipPosition.y+10,
                            left: tooltipPosition.x+10,
                            zIndex: 10,
                            backgroundColor: "rgba(0, 0, 0, 0.8)",
                            color: "white",
                            padding: "8px",
                            borderRadius: "20px",
                        }}
                    className={`border-4  ${tooltipContent?.element === "fire"
                    ? "border-red-500"
                    : tooltipContent?.element === "water"
                    ? "border-cyan-500"
                    : tooltipContent?.element === "nature"
                    ? "border-green-400"
                    : tooltipContent?.element === "light"
                    ? "border-yellow-500"
                    : tooltipContent?.element === "dark"
                    ? "border-purple-900"
                    : "border-white"} `}
                    >
                        <h4 className="text-2xl font-bold">{tooltipContent?.name}</h4>
                        <img className="w-20 h-20" src={`images/${tooltipContent?.photo}.png`}></img>
                        <p className="text-lg font-bold">TYPE: {tooltipContent?.type} </p>
                        {tooltipContent?.power !== 0  &&(
                        <p>POWER: +{tooltipContent?.power}</p>)}
                        {tooltipContent?.vitality !== 0  &&(
                        <p>VITALITY: +{tooltipContent?.vitality}</p>)}
                        {tooltipContent?.strength !== 0  &&(
                        <p>STRENGTH: +{tooltipContent?.strength}</p>)}
                        {tooltipContent?.dexterity !== 0  &&(
                        <p>DEXTERITY: +{tooltipContent?.dexterity}</p>)}
                        {tooltipContent?.intelligence !== 0  &&(
                        <p>INTELLIGENCE: +{tooltipContent?.intelligence}</p>)}
                        {tooltipContent?.armor !== 0  &&(
                        <p>ARMOR: +{tooltipContent?.armor}</p>)}
                        <p><span>ELEMENT: </span>{" "}<span className={` ${
                        tooltipContent?.element === "fire"
                            ? "text-red-500"
                            : tooltipContent?.element === "water"
                            ? "text-cyan-500"
                            : tooltipContent?.element === "nature"
                            ? "text-green-400"
                            : tooltipContent?.element === "light"
                            ? "text-yellow-500"
                            : tooltipContent?.element === "dark"
                            ? "text-purple-900"
                            : "text-white"} font-bold`}>{tooltipContent?.element}</span></p>
            
                    </div>
                    )}
                </div>
            )}
        </>
        ): (
      <div>Loading...</div> // Wywietlanie komunikatu adowania
    )}

        {/*Modal do wystawiania przedmiot贸w */}
        {isSellModalOpen && selectedItem && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center">
            <div className="bg-maincolor1 p-6 rounded-lg shadow-md w-1/4">
                <h2 className="text-lg font-bold mb-4 text-maincolor4">Sprzedaj lub Licytuj</h2>
                <h3 className="font-bold mb-4 text-maincolor4">{selectedItem.name}</h3>
                <label className="block mb-2 text-maincolor4">Cena</label>
                <input
                    type="number"
                    value={sellData.price}
                    onChange={(e) => setSellData({ ...sellData, price: e.target.value})}
                    className="w-full p-2 border rounded mb-4 text-black"
                />
                <label className="block mb-2 text-maincolor4">Czas trwania</label>
                <select
                    value={sellData.duration}
                    onChange={(e) => setSellData({ ...sellData, duration: e.target.value })}
                    className="w-full p-2 border rounded mb-4 text-black"
                >
                    <option value="2">2 godziny</option>
                    <option value="8">8 godzin</option>
                    <option value="24">24 godziny</option>
                </select>
                <div className="flex flex-col md:flex-row justify-end mt-4 p-4 space-y-2 md:space-y-0 md:space-x-2">
                    <button
                        onClick={() => handleSell('fixed')}
                        className="border-maincolor2 rounded-xl text-maincolor4 border py-2 px-4 w-full md:w-1/3 hover:border-maincolor5 hover:shadow-maincolor5 hover:bg-maincolor4 shadow-buttonshadow transition duration-300 hover:text-black1 hover:bg-opacity-75"
                    >
                        Sprzedaj
                    </button>
                    <button
                        onClick={() => handleSell('auction')}
                        className="border-maincolor2 rounded-xl text-maincolor4 border py-2 px-4 w-full md:w-1/3 hover:border-maincolor5 hover:shadow-maincolor5 hover:bg-maincolor4 shadow-buttonshadow transition duration-300 hover:text-black1 hover:bg-opacity-75"
                    >
                        Licytuj
                    </button>
                    <button
                        onClick={() =>{
                            setIsSellModalOpen(false);
                            setSelectedItem(null);
                        }}
                        className="border-maincolor2 rounded-xl text-maincolor4 border py-2 px-4 w-full md:w-1/3 hover:border-maincolor5 hover:shadow-maincolor5 hover:bg-maincolor4 shadow-buttonshadow transition duration-300 hover:text-black1 hover:bg-opacity-75"
                    >
                        Anuluj
                    </button>
                </div>
            </div>
        </div>
        )}
        
    </div>
    );
};

export default Market;
