import React, { useState, useEffect } from "react";

const Market = () => {
    const [activeTab, setActiveTab] = useState("market"); // Zakładki: "market" lub "sell"
    const [marketItems, setMarketItems] = useState([]);
    const [formData, setFormData] = useState({
        itemId: "",
        type: "fixed",
        price: "",
        duration: "2"
    });

    useEffect(() => {
        fetchMarketItems();
    }, []);

    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/items", {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów:', response.statusText);
                return;
            }

            const data = await response.json();
            setMarketItems(data);
        } catch (error) {
            console.error('Błąd pobierania przedmiotów:', error);
        }
    };

    const handleFormChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/sell", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(formData)
            });

            if (!response.ok) {
                console.error('Błąd wystawiania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został wystawiony na sprzedaż!');
            fetchMarketItems();
        } catch (error) {
            console.error('Błąd wystawiania przedmiotu:', error);
        }
    };

    const handleBuy = async (marketItemId) => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch(`http://localhost:8080/api/market/buy`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ marketItemId })
            });

            if (!response.ok) {
                console.error('Błąd kupowania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został kupiony!');
            fetchMarketItems();
        } catch (error) {
            console.error('Błąd kupowania przedmiotu:', error);
        }
    };

    return (
        <div className="p-8">
            <div className="flex justify-around mb-8">
                <button onClick={() => setActiveTab("market")} className="text-xl font-bold text-blue-500">Market</button>
                <button onClick={() => setActiveTab("sell")} className="text-xl font-bold text-blue-500">Sell Item</button>
            </div>

            {activeTab === "market" && (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    {marketItems.map(item => (
                        <div key={item._id} className="border p-4 rounded-lg shadow-lg">
                            <h3 className="font-bold text-lg">{item.item?.name || 'Unnamed Item'}</h3>
                            <p>Type: {item.type === 'fixed' ? 'Sale' : 'Auction'}</p>
                            <p>Price: {item.type === 'fixed' ? item.buyoutPrice : item.currentBid || item.startingPrice}</p>
                            <button 
                                onClick={() => handleBuy(item._id)} 
                                className="bg-green-500 text-white px-4 py-2 rounded mt-2"
                            >
                                {item.type === 'fixed' ? 'Buy Now' : 'Bid'}
                            </button>
                        </div>
                    ))}
                </div>
            )}

            {activeTab === "sell" && (
                <form onSubmit={handleSubmit} className="max-w-lg mx-auto">
                    <div className="mb-4">
                        <label className="block mb-2">Item ID</label>
                        <input 
                            type="text" 
                            name="itemId" 
                            value={formData.itemId} 
                            onChange={handleFormChange} 
                            className="w-full border p-2 rounded"
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block mb-2">Type</label>
                        <select 
                            name="type" 
                            value={formData.type} 
                            onChange={handleFormChange} 
                            className="w-full border p-2 rounded"
                        >
                            <option value="fixed">Fixed Price</option>
                            <option value="auction">Auction</option>
                        </select>
                    </div>
                    <div className="mb-4">
                        <label className="block mb-2">Price</label>
                        <input 
                            type="number" 
                            name="price" 
                            value={formData.price} 
                            onChange={handleFormChange} 
                            className="w-full border p-2 rounded"
                            required
                        />
                    </div>
                    <div className="mb-4">
                        <label className="block mb-2">Duration (hours)</label>
                        <select 
                            name="duration" 
                            value={formData.duration} 
                            onChange={handleFormChange} 
                            className="w-full border p-2 rounded"
                        >
                            <option value="2">2 hours</option>
                            <option value="8">8 hours</option>
                            <option value="24">24 hours</option>
                        </select>
                    </div>
                    <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">List Item</button>
                </form>
            )}
        </div>
    );
};

export default Market;
