import React, { useState, useEffect } from "react";

const Market = () => {
    const [activeTab, setActiveTab] = useState("market"); // Domyślnie otwieramy Market
    const [tooltipVisible, setTooltipVisible] = useState(false);
    const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });
    const [tooltipContent, setTooltipContent] = useState(null);
    const [userItems, setUserItems] = useState([]); // Przedmioty użytkownika
    const [marketItems, setMarketItems] = useState([]); // Przedmioty na markecie
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });

    useEffect(() => {
        fetchMarketItems();
        fetchUserItems();
    }, []);

    /** 🔥 Poprawione pobieranie przedmiotów z marketu */
    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów na markecie:', response.statusText);
                return;
            }

            const data = await response.json();
            console.log("✅ Market items:", data);
            setMarketItems(data);
        } catch (error) {
            console.error('❌ Błąd pobierania przedmiotów na markecie:', error);
        }
    };

    /** 🔥 Poprawione pobieranie ekwipunku gracza */
    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/usersCreaturesAndItemsData", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów użytkownika:', response.statusText);
                return;
            }

            const data = await response.json();
            setUserItems(data?.userItems || []);
        } catch (error) {
            console.error('❌ Błąd pobierania przedmiotów użytkownika:', error);
        }
    };

    /** 🔥 Obsługa sprzedaży */
    const handleSell = async () => {
        const token = localStorage.getItem('token');
        if (!token || !selectedItem) {
            console.warn('Brak tokenu lub nie wybrano przedmiotu');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/sell", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    ...sellData,
                    itemId: selectedItem._id
                })
            });

            if (!response.ok) {
                console.error('Błąd wystawiania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został wystawiony!');
            fetchMarketItems(); // Odśwież market
            fetchUserItems(); // Odśwież ekwipunek
            setIsSellModalOpen(false);
        } catch (error) {
            console.error('❌ Błąd wystawiania przedmiotu:', error);
        }
    };
    const handleMouseEnter = (actualEvent, item) =>{
        const rect = actualEvent.currentTarget.getBoundingClientRect();
        setTooltipPosition({ x: actualEvent.pageX, y: actualEvent.pageY });
        setTooltipContent(item);
        setTooltipVisible(true);
    };
    
    const handleMouseMove = (event) => {
        setTooltipPosition({ x: event.pageX, y: event.pageY }); // Aktualizuj pozycję tooltipa podczas ruchu myszy
    };
    const handleMouseLeave = () => {
        setTooltipVisible(false);
    }

    return (
        <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">
            {/* Panel nawigacyjny */}
            <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg sticky top-0 z-10">
                <button onClick={() => setActiveTab("market")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Market
                </button>
                <button onClick={() => setActiveTab("inventory")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Inventory
                </button>
            </div>

            {/* Widok Marketu */}
            {activeTab === "market" && (
                <div className="flex-grow overflow-y-auto">
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 max-h-[60vh]">
                        {marketItems.length === 0 ? (
                            <p className="text-maincolor4 text-center">Brak przedmiotów na rynku.</p>
                        ) : (
                            marketItems.map((item) => (
                                <div key={item._id} 
                                    className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all"
                                    onMouseEnter={(event) => handleMouseEnter(event, item.item)}
                                    onMouseLeave={handleMouseLeave}
                                    onMouseMove={handleMouseMove}
                                >
                                    <img src={`images/${item.item.photo}.png`} alt={item.item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                    <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.item.name}</h3>
                                    <p className="text-maincolor4 font-bold mt-2">Price: {item.item.buyoutPrice || item.item.startingPrice} coins</p>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            )}

            {/* Widok Inventory */}
            {activeTab === "inventory" && (
                <div className="flex-grow overflow-y-auto">
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {userItems.length === 0 ? (
                            <p className="text-maincolor4 text-center">Twój inventarz jest pusty!</p>
                        ) : (
                            userItems.map((item) => (
                                <div key={item._id} 
                                    className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all"
                                    onMouseEnter={(event) => handleMouseEnter(event, item)}
                                    onMouseLeave={handleMouseLeave}
                                    onMouseMove={handleMouseMove}
                                >
                                    <img src={`images/${item.photo}.png`} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                    <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                                    <p className="text-maincolor4 font-bold mt-2">Sell value: {Math.round(item.price / 3)} coins</p>
                                    <button
                                        onClick={() => { setSelectedItem(item); setIsSellModalOpen(true); }}
                                        className="mt-3 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all"
                                    >
                                        Sell item
                                    </button>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            )}

            {/*Modal do wystawiania przedmiotów */}
        {isSellModalOpen && selectedItem && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center">
            <div className="bg-maincolor1 p-6 rounded-lg shadow-md w-1/4">
                <h2 className="text-lg font-bold mb-4 text-maincolor4">Sprzedaj lub Licytuj</h2>
                <h3 className="font-bold mb-4 text-maincolor4">{selectedItem.name}</h3>
                <label className="block mb-2 text-maincolor4">Cena</label>
                <input
                    type="number"
                    value={sellData.price}
                    onChange={(e) => setSellData({ ...sellData, price: e.target.value})}
                    className="w-full p-2 border rounded mb-4 text-black"
                />
                <label className="block mb-2 text-maincolor4">Czas trwania</label>
                <select
                    value={sellData.duration}
                    onChange={(e) => setSellData({ ...sellData, duration: e.target.value })}
                    className="w-full p-2 border rounded mb-4 text-black"
                >
                    <option value="2">2 godziny</option>
                    <option value="8">8 godzin</option>
                    <option value="24">24 godziny</option>
                </select>
                <div className="flex flex-col md:flex-row justify-end mt-4 p-4 space-y-2 md:space-y-0 md:space-x-2">
                    <button
                        onClick={() => handleSell('fixed')}
                        className="border-maincolor2 rounded-xl text-maincolor4 border py-2 px-4 w-full md:w-1/3 hover:border-maincolor5 hover:shadow-maincolor5 hover:bg-maincolor4 shadow-buttonshadow transition duration-300 hover:text-black1 hover:bg-opacity-75"
                    >
                        Sprzedaj
                    </button>
                    <button
                        onClick={() => handleSell('auction')}
                        className="border-maincolor2 rounded-xl text-maincolor4 border py-2 px-4 w-full md:w-1/3 hover:border-maincolor5 hover:shadow-maincolor5 hover:bg-maincolor4 shadow-buttonshadow transition duration-300 hover:text-black1 hover:bg-opacity-75"
                    >
                        Licytuj
                    </button>
                    <button
                        onClick={() =>{
                            setIsSellModalOpen(false);
                            setSelectedItem(null);
                        }}
                        className="border-maincolor2 rounded-xl text-maincolor4 border py-2 px-4 w-full md:w-1/3 hover:border-maincolor5 hover:shadow-maincolor5 hover:bg-maincolor4 shadow-buttonshadow transition duration-300 hover:text-black1 hover:bg-opacity-75"
                    >
                        Anuluj
                    </button>
                </div>
            </div>
        </div>
        )}
          {/* Tooltip */}
            {tooltipVisible && tooltipContent && (
                <div
                    style={{
                        position: "fixed",
                        top: tooltipPosition.y + 10,
                        left: tooltipPosition.x + 10,
                        zIndex: 10,
                        backgroundColor: "rgba(0, 0, 0, 0.8)",
                        color: "white",
                        padding: "8px",
                        borderRadius: "10px",
                    }}
                    className={`border-4 ${
                        tooltipContent.element === "fire"
                        ? "border-red-500"
                        : tooltipContent.element === "water"
                        ? "border-cyan-500"
                        : tooltipContent.element === "nature"
                        ? "border-green-400"
                        : tooltipContent.element === "light"
                        ? "border-yellow-500"
                        : tooltipContent.element === "dark"
                        ? "border-purple-900"
                        : "border-white"
                    } `}
                >
                    <h4 className="text-2xl font-bold">{tooltipContent.name}</h4>
                    <img className="w-20 h-20" src={`images/${tooltipContent.photo}.png`} alt="item tooltip" />
                    <p className="text-lg font-bold">TYPE: {tooltipContent.type} </p>
                    {tooltipContent.power !== 0 && <p>POWER: +{tooltipContent.power}</p>}
                    {tooltipContent.vitality !== 0 && <p>VITALITY: +{tooltipContent.vitality}</p>}
                    {tooltipContent.strength !== 0 && <p>STRENGTH: +{tooltipContent.strength}</p>}
                    {tooltipContent.dexterity !== 0 && <p>DEXTERITY: +{tooltipContent.dexterity}</p>}
                    {tooltipContent.intelligence !== 0 && <p>INTELLIGENCE: +{tooltipContent.intelligence}</p>}
                    {tooltipContent.armor !== 0 && <p>ARMOR: +{tooltipContent.armor}</p>}
                    <p>
                        <span>ELEMENT: </span>
                        <span
                            className={` ${
                                tooltipContent.element === "fire"
                                    ? "text-red-500"
                                    : tooltipContent.element === "water"
                                    ? "text-cyan-500"
                                    : tooltipContent.element === "nature"
                                    ? "text-green-400"
                                    : tooltipContent.element === "light"
                                    ? "text-yellow-500"
                                    : tooltipContent.element === "dark"
                                    ? "text-purple-900"
                                    : "text-white"
                            } font-bold`}
                        >
                            {tooltipContent.element}
                        </span>
                    </p>
                </div>
            )}
        </div>
    );
};

export default Market;
