import React, { useState } from 'react';

const ItemModal = ({ type, closeModal, fetchItems }) => {
    const [itemId, setItemId] = useState('');
    const [price, setPrice] = useState('');
    const [duration, setDuration] = useState('');
    const [startingPrice, setStartingPrice] = useState('');
    const token = localStorage.getItem('token');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const body = {
            itemId,
            type,
            price: type === 'sell' ? price : undefined,
            startingPrice: type === 'auction' ? startingPrice : undefined,
            duration,
        };

        try {
            const response = await fetch('http://localhost:8080/api/market/add', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify(body),
            });

            const data = await response.json();
            if (response.ok) {
                alert('Przedmiot został wystawiony!');
                fetchItems(); // Odśwież listę przedmiotów
                closeModal();
            } else {
                alert(`Błąd: ${data.error}`);
            }
        } catch (error) {
            console.error('Błąd serwera:', error);
        }
    };

    return (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded shadow-lg w-96">
                <h2 className="text-lg font-bold mb-4">
                    {type === 'sell' ? 'Wystaw na sprzedaż' : 'Wystaw na licytację'}
                </h2>
                <form onSubmit={handleSubmit}>
                    <label className="block mb-2">
                        ID Przedmiotu:
                        <input
                            type="text"
                            className="border w-full p-2 rounded mt-1"
                            value={itemId}
                            onChange={(e) => setItemId(e.target.value)}
                        />
                    </label>
                    {type === 'sell' && (
                        <label className="block mb-2">
                            Cena:
                            <input
                                type="number"
                                className="border w-full p-2 rounded mt-1"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                            />
                        </label>
                    )}
                    {type === 'auction' && (
                        <label className="block mb-2">
                            Cena wywoławcza:
                            <input
                                type="number"
                                className="border w-full p-2 rounded mt-1"
                                value={startingPrice}
                                onChange={(e) => setStartingPrice(e.target.value)}
                            />
                        </label>
                    )}
                    <label className="block mb-2">
                        Czas trwania (godziny):
                        <input
                            type="number"
                            className="border w-full p-2 rounded mt-1"
                            value={duration}
                            onChange={(e) => setDuration(e.target.value)}
                        />
                    </label>
                    <div className="flex justify-end">
                        <button
                            type="button"
                            onClick={closeModal}
                            className="bg-gray-500 text-white px-4 py-2 rounded mr-2"
                        >
                            Anuluj
                        </button>
                        <button
                            type="submit"
                            className="bg-green-500 text-white px-4 py-2 rounded"
                        >
                            Wystaw
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default ItemModal;
