import React, { useState, useEffect } from "react";

const Market = () => {
    const [activeTab, setActiveTab] = useState("shop");
    const [shopItems, setShopItems] = useState([]);
    const [userItems, setUserItems] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });

    useEffect(() => {
        fetchShopData();
        fetchUserItems();
    }, []);

    const fetchShopData = async () => {
        const token = localStorage.getItem('token');
        if (!token) return;
        try {
            const response = await fetch("http://localhost:8080/api/ItemShop", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!response.ok) return;
            const data = await response.json();
            setShopItems(data.ShopItems);
            setUserItems(data.userItems);
        } catch (error) {
            console.error('Błąd pobierania sklepu:', error);
        }
    };

    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) return;
        try {
            const response = await fetch("http://localhost:8080/api/user/items", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });
            if (!response.ok) return;
            const data = await response.json();
            setUserItems(data);
        } catch (error) {
            console.error('Błąd pobierania przedmiotów użytkownika:', error);
        }
    };

    const isSellModalOpenClick = (item) => {
        setSelectedItem(item);
        setIsSellModalOpen(true);
    };

    return (
        <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">
            <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg sticky top-0 z-10">
                <button onClick={() => setActiveTab("shop")} className="text-maincolor4 text-lg hover:text-blue-600">Shop</button>
                <button onClick={() => setActiveTab("inventory")} className="text-maincolor4 text-lg hover:text-blue-600">Inventory</button>
            </div>
            {activeTab === "shop" && (
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 max-h-[60vh]">
                    {shopItems.map((item) => (
                        <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl">
                            <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                            <p className="text-maincolor4">Price: {item.price} coins</p>
                        </div>
                    ))}
                </div>
            )}
            {activeTab === "inventory" && (
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                    {userItems.map((item) => (
                        <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl">
                            <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                            <p className="text-maincolor4">Sell value: {Math.round(item.price / 3)} coins</p>
                            <button onClick={() => isSellModalOpenClick(item)} className="mt-3 bg-blue-600 text-white py-2 px-4 rounded-lg">Sell item</button>
                        </div>
                    ))}
                </div>
            )}
            {isSellModalOpen && (
                <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-70 z-50">
                    <div className="bg-maincolor1 p-8 rounded-lg shadow-xl max-w-lg mx-auto text-maincolor4 relative">
                        <h2 className="text-2xl font-bold mb-6">Sprzedaj lub Licytuj</h2>
                        {selectedItem && (
                            <div>
                                <h3 className="font-bold mb-4">{selectedItem.name}</h3>
                                <label className="block mb-2">Cena</label>
                                <input
                                    type="number"
                                    value={sellData.price}
                                    onChange={(e) => setSellData({ ...sellData, price: e.target.value })}
                                    className="w-full p-2 border rounded mb-4 text-black"
                                />
                                <label className="block mb-2">Czas trwania (godziny)</label>
                                <select
                                    value={sellData.duration}
                                    onChange={(e) => setSellData({ ...sellData, duration: e.target.value })}
                                    className="w-full p-2 border rounded mb-4 text-black"
                                >
                                    <option value="2">2 godziny</option>
                                    <option value="8">8 godzin</option>
                                    <option value="24">24 godziny</option>
                                </select>
                                <button
                                    onClick={() => setIsSellModalOpen(false)}
                                    className="absolute top-2 right-4 text-white text-xl font-bold"
                                >
                                    ×
                                </button>
                                <button
                                    onClick={() => console.log("Sprzedano!")}
                                    className="border-maincolor2 mb-4 rounded-xl border py-2 px-4 w-full hover:border-maincolor5 hover:bg-maincolor4"
                                >
                                    Sprzedaj
                                </button>
                                <button
                                    onClick={() => console.log("Licytacja!")}
                                    className="border-maincolor2 rounded-xl border py-2 px-4 w-full hover:border-maincolor5 hover:bg-maincolor4"
                                >
                                    Licytuj
                                </button>
                            </div>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Market;
