import React, { useState, useEffect } from "react";
import SellModal from "./SellModal";
import AuctionModal from "./AuctionModal";

function MarketAddItem() {
  const [inventory, setInventory] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showSellModal, setShowSellModal] = useState(false);
  const [showAuctionModal, setShowAuctionModal] = useState(false);

  useEffect(() => {
    const fetchInventory = async () => {
      try {
        const response = await fetch("http://localhost:8080/api/user/inventory", {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        const data = await response.json();
        setInventory(data);
      } catch (error) {
        console.error("Error fetching inventory:", error);
      }
    };

    fetchInventory();
  }, []);

  const handleSell = (item) => {
    setSelectedItem(item);
    setShowSellModal(true);
  };

  const handleAuction = (item) => {
    setSelectedItem(item);
    setShowAuctionModal(true);
  };

  return (
    <div className="p-4 bg-gray-900 min-h-screen text-white">
      <h1 className="text-3xl font-bold mb-4">Your Inventory</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {inventory.map((item) => (
          <div key={item._id} className="bg-gray-800 p-4 rounded-lg shadow-md">
            <img
              src={`images/${item.photo}.png`}
              alt={item.name}
              className="w-full h-32 object-cover rounded mb-4"
            />
            <h2 className="text-xl font-bold">{item.name}</h2>
            <div className="mt-2 flex space-x-2">
              <button
                onClick={() => handleSell(item)}
                className="bg-green-500 px-4 py-2 rounded text-black"
              >
                Sell
              </button>
              <button
                onClick={() => handleAuction(item)}
                className="bg-blue-500 px-4 py-2 rounded text-black"
              >
                Auction
              </button>
            </div>
          </div>
        ))}
      </div>

      {showSellModal && (
        <SellModal
          item={selectedItem}
          onClose={() => setShowSellModal(false)}
        />
      )}
      {showAuctionModal && (
        <AuctionModal
          item={selectedItem}
          onClose={() => setShowAuctionModal(false)}
        />
      )}
    </div>
  );
}

export default MarketAddItem;
