import React, { useState, useEffect } from "react";

function Market() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    fetchItems(); // Pobranie danych z API
  }, []);

  const fetchItems = async () => {
    // Pobierz dane z backendu
    const response = await fetch("http://localhost:8080/api/market");
    const data = await response.json();
    setItems(data);
  };

  return (
    <div className="p-6 bg-gray-900 text-white">
      <h2 className="text-2xl font-bold mb-4">Market</h2>
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {items.map((item) => (
          <div key={item._id} className="bg-gray-800 p-4 rounded-lg shadow-md">
            <img
              src={`images/${item.photo}.png`}
              alt={item.name}
              className="w-full h-32 object-cover rounded-md"
            />
            <h3 className="text-lg font-bold mt-2">{item.name}</h3>
            <p>Price: {item.price} coins</p>
            <button className="mt-2 w-full bg-blue-600 hover:bg-blue-800 text-white py-2 rounded-lg">
              Buy
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Market;
