import React, { useState, useEffect } from 'react';
import MarketItemCard from './MarketItemCard';
import ItemModal from './ItemModal';

const MarketPage = () => {
    const [items, setItems] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalType, setModalType] = useState('sell'); // 'sell' lub 'auction'

    useEffect(() => {
        fetch('/api/market')
            .then((response) => response.json())
            .then((data) => setItems(data))
            .catch((error) => console.error('Błąd przy pobieraniu przedmiotów:', error));
    }, []);

    const openModal = (type) => {
        setModalType(type);
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">Market</h1>
            <button
                onClick={() => openModal('sell')}
                className="bg-green-500 text-white px-4 py-2 rounded mb-4"
            >
                Wystaw przedmiot na sprzedaż
            </button>
            <button
                onClick={() => openModal('auction')}
                className="bg-blue-500 text-white px-4 py-2 rounded ml-2"
            >
                Wystaw przedmiot na licytację
            </button>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {items.map((item) => (
                    <MarketItemCard key={item._id} item={item} />
                ))}
            </div>

            {isModalOpen && <ItemModal type={modalType} closeModal={closeModal} />}
        </div>
    );
};

export default MarketPage;
