const fetchMarketItems = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
        console.warn('Brak tokenu w localStorage');
        return;
    }

    try {
        const response = await fetch("http://localhost:8080/api/market", {  // ✅ Poprawiony endpoint
            method: 'GET',
            headers: { 'Authorization': `Bearer ${token}` }
        });

        if (!response.ok) {
            console.error('Błąd pobierania przedmiotów na markecie:', response.statusText);
            return;
        }

        const data = await response.json();
        console.log("Market items:", data);
        setMarketItems(data || []);
    } catch (error) {
        console.error('Błąd pobierania przedmiotów na markecie:', error);
    }
};

const fetchUserItems = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
        console.warn('Brak tokenu w localStorage');
        return;
    }

    try {
        const response = await fetch("http://localhost:8080/api/usersCreaturesAndItemsData", {  // ✅ Poprawiony endpoint
            method: 'GET',
            headers: { 'Authorization': `Bearer ${token}` }
        });

        if (!response.ok) {
            console.error('Błąd pobierania przedmiotów użytkownika:', response.statusText);
            return;
        }

        const data = await response.json();
        console.log("User items:", data);
        setUserItems(data?.userItems || []);
    } catch (error) {
        console.error('Błąd pobierania przedmiotów użytkownika:', error);
    }
};
