import React, {useState} from "react";

const FilterBar = ({onFilterChange}) => {
    const [category, setCategory] = useState('');
    const [element, setElement] = useState('');

    const handleFilterChange = () => {
        onFilterChange({category, element});
    };

    return(
        <div className="filter-bar mb-4">
            <select value={category} onChange={(e) => setCategory(e.target.value)}>
                <option value="">Wszystkie kategorie</option>
                <option value="equipable">Wyposarzenie</option>
                <option value="usable">Używalne</option>
                <option value="orb">Orby</option>
            </select>

            <select value={element} onChange={(e) => setElement(e.target.value)}>
            <option value="">Wszystkie elementy</option>
            <option value="fire">Ogień</option>
            <option value="water">Woda</option>
            <option value="nature">Natura</option>
            <option value="light">Światło</option>
            <option value="dark">Ciemność</option>
            </select>

            <button onClick={handleFilterChange} className="bg-blue-500 text-maincolor4 px-4 py-2 ml-2 rounded">
                Filtruj
            </button>
        </div>
    );
};

export default FilterBar;
