import React, { useState, useEffect } from "react";

const Market = () => {
    const [activeTab, setActiveTab] = useState("market"); // Domyślnie ustawiamy zakładkę "market"
    const [marketItems, setMarketItems] = useState([]); // Przedmioty na markecie
    const [userItems, setUserItems] = useState([]); // Przedmioty użytkownika
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });

    useEffect(() => {
        fetchMarketItems();
        fetchUserItems();
    }, []);

    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/items", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów:', response.statusText);
                return;
            }

            const data = await response.json();
            setMarketItems(data);
        } catch (error) {
            console.error('Błąd pobierania przedmiotów:', error);
        }
    };

    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/user/items", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów użytkownika:', response.statusText);
                return;
            }

            const data = await response.json();
            setUserItems(data);
        } catch (error) {
            console.error('Błąd pobierania przedmiotów użytkownika:', error);
        }
    };

    const handleSell = async () => {
        const token = localStorage.getItem('token');
        if (!token || !selectedItem) {
            console.warn('Brak tokenu lub nie wybrano przedmiotu');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/sell", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    ...sellData,
                    itemId: selectedItem._id,
                })
            });

            if (!response.ok) {
                console.error('Błąd wystawiania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został wystawiony!');
            fetchMarketItems();
            fetchUserItems(); // Aktualizujemy listę przedmiotów użytkownika
            setIsSellModalOpen(false);
        } catch (error) {
            console.error('Błąd wystawiania przedmiotu:', error);
        }
    };

    return (
        <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">      
            <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg sticky top-0 z-10">
                <button onClick={() => setActiveTab("market")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Market
                </button>
                <button onClick={() => setActiveTab("inventory")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Inventory
                </button>
            </div>

            {/* Widok marketu */}
            {activeTab === "market" && (
                <div className="flex-grow overflow-y-auto">
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 max-h-[60vh]">
                        {marketItems.map((item) => (
                            <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all">
                                <img src={`images/${item.photo}.png`} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                                <p className="text-maincolor4 text-sm mb-2">{item.description}</p>
                                <p className="text-maincolor4 font-bold mt-2">Price: {item.price} coins</p>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            {/* Widok inwentarza */}
            {activeTab === "inventory" && (
                <div className="flex-grow overflow-y-auto">
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {userItems.length === 0 ? (
                            <p className="text-maincolor4 text-center">Twój inventarz jest pusty!</p>
                        ) : (
                            userItems.map((item) => (
                                <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all">
                                    <img src={`images/${item.photo}.png`} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                    <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                                    <p className="text-maincolor4 text-sm mb-2">{item.description}</p>
                                    <p className="text-maincolor4 font-bold mt-2">Sell value: {Math.round(item.price / 3)} coins</p>
                                    <button
                                        onClick={() => { setSelectedItem(item); setIsSellModalOpen(true); }}
                                        className="mt-3 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all"
                                    >
                                        Sell item
                                    </button>
                                </div>
                            ))
                        )}
                    </div>
                </div>
            )}

            {/* Modal sprzedaży */}
            {isSellModalOpen && selectedItem && (
                <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center">
                    <div className="bg-maincolor1 p-6 rounded-lg shadow-md w-1/4">
                        <h2 className="text-lg font-bold mb-4 text-maincolor4">Sprzedaj przedmiot</h2>
                        <h3 className="font-bold mb-4 text-maincolor4">{selectedItem.name}</h3>
                        <label className="block mb-2 text-maincolor4">Cena</label>
                        <input type="number" value={sellData.price} onChange={(e) => setSellData({ ...sellData, price: e.target.value})} className="w-full p-2 border rounded mb-4 text-black" />
                        <button onClick={handleSell} className="w-full bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all">Wystaw na sprzedaż</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Market;
