import React, { useState, useEffect } from 'react';

const SellModal = ({ onClose, refreshMarket }) => {
    const [userItems, setUserItems] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [type, setType] = useState('fixed');
    const [price, setPrice] = useState('');
    const [startingPrice, setStartingPrice] = useState('');
    const [duration, setDuration] = useState(2); // Default: 2 hours
    const token = localStorage.getItem('token');

    // Funkcja pobierająca przedmioty użytkownika
    const fetchUserItems = async () => {
        try {
            const response = await fetch('http://localhost:8080/api/userData', {
                method: 'GET',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (response.ok) {
                const data = await response.json();
                setUserItems(data.items);
            } else {
                console.error('Błąd podczas pobierania przedmiotów użytkownika.');
            }
        } catch (error) {
            console.error('Błąd serwera:', error);
        }
    };

    useEffect(() => {
        fetchUserItems();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!selectedItem) {
            alert('Wybierz przedmiot do sprzedaży.');
            return;
        }

        const payload = {
            itemId: selectedItem._id,
            type,
            price: type === 'fixed' ? price : undefined,
            startingPrice: type === 'auction' ? startingPrice : undefined,
            duration,
        };

        try {
            const response = await fetch('http://localhost:8080/api/market/add', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${token}`,
                },
                body: JSON.stringify(payload),
            });

            if (response.ok) {
                alert('Przedmiot wystawiony na rynek!');
                refreshMarket();
                onClose();
            } else {
                const errorData = await response.json();
                alert(`Błąd: ${errorData.error}`);
            }
        } catch (error) {
            console.error('Błąd serwera:', error);
        }
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
                <h2 className="text-xl font-bold mb-4">Wystaw przedmiot</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label className="block font-bold">Przedmiot:</label>
                        <select
                            value={selectedItem?._id || ''}
                            onChange={(e) => {
                                const item = userItems.find((item) => item._id === e.target.value);
                                setSelectedItem(item);
                            }}
                            className="border p-2 rounded w-full"
                        >
                            <option value="">-- Wybierz przedmiot --</option>
                            {userItems.map((item) => (
                                <option key={item._id} value={item._id}>
                                    {item.name} (Cena: {item.price})
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="mb-4">
                        <label className="block font-bold">Typ:</label>
                        <select
                            value={type}
                            onChange={(e) => setType(e.target.value)}
                            className="border p-2 rounded w-full"
                        >
                            <option value="fixed">Sprzedaż</option>
                            <option value="auction">Licytacja</option>
                        </select>
                    </div>

                    {type === 'fixed' && (
                        <div className="mb-4">
                            <label className="block font-bold">Cena:</label>
                            <input
                                type="number"
                                value={price}
                                onChange={(e) => setPrice(e.target.value)}
                                className="border p-2 rounded w-full"
                                required
                            />
                        </div>
                    )}

                    {type === 'auction' && (
                        <div className="mb-4">
                            <label className="block font-bold">Cena startowa:</label>
                            <input
                                type="number"
                                value={startingPrice}
                                onChange={(e) => setStartingPrice(e.target.value)}
                                className="border p-2 rounded w-full"
                                required
                            />
                        </div>
                    )}

                    <div className="mb-4">
                        <label className="block font-bold">Czas trwania (godziny):</label>
                        <select
                            value={duration}
                            onChange={(e) => setDuration(Number(e.target.value))}
                            className="border p-2 rounded w-full"
                        >
                            <option value={2}>2 godziny</option>
                            <option value={8}>8 godzin</option>
                            <option value={24}>24 godziny</option>
                        </select>
                    </div>

                    <div className="flex justify-end space-x-2">
                        <button
                            type="button"
                            onClick={onClose}
                            className="bg-red-500 text-white px-4 py-2 rounded shadow hover:bg-red-600"
                        >
                            Anuluj
                        </button>
                        <button
                            type="submit"
                            className="bg-blue-500 text-white px-4 py-2 rounded shadow hover:bg-blue-600"
                        >
                            Wystaw
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default SellModal;
