import React, { useState, useEffect } from 'react';

function Ranking(){
    const [rankingTable, setRankingTable] = useState([]);
        const [positon, setPosition] = useState(1);
    fetchById = async () =>{
        
        const token = localStorage.getItem('token'); // Pobranie tokena z localStorage
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }
    
        try {
            const response = await fetch('http://localhost:8080/api/RankingForUserById ', {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`, // Wysłanie tokena w nagłówku
                },
            });
    
            if (!response.ok) {
                console.error('Błąd pobierania danych użytkownika:', response.statusText);
                return;
            } 
            const data = await response.json();
            setPosition(data.userRank);
            setRankingTable(data.ranking.rankingTable);
        } catch (error) {
            console.error('Błąd podczas pobierania danych użytkownika:', error);
        } 
    }
    useEffect(()=>{

    })
    const playersplace = Array(13).fill("");
    return(
    <><div className='text-maincolor4 w-1/2 h-[96%] m-[1vw] bg-gradient-to-r from-maincolor1 via-black to-maincolor1 border-2 border-maincolor1 rounded-2xl'>
        {playersplace.map((_,index)=>(
            <div className='w-[97%] h-[6.2%] m-[0.5vw] border-2 border-black bg-gradient-to-r from-maincolor1 to-black rounded-xl'>
                
                </div>
        ))

        }
        <div className='w-full  h-[4%] flex justify-between text-maincolor5'>
            <button className='ml-[2vw] text-5xl'>◂</button>
            <button className='mr-[2vw] text-5xl'>▸</button>
            </div>
    </div></>);
}
export default Ranking
