import React, { useState, useEffect, useRef } from 'react';

function Chat() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const chatRef = useRef(null);

  // Automatycznie przewijaj czat na dół po każdej nowej wiadomości
  useEffect(() => {
    chatRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const message = {
        id: Date.now(),
        text: newMessage,
        sender: 'me', // wiadomość od użytkownika
      };
      setMessages((prevMessages) => [...prevMessages, message]);
      setNewMessage('');
      simulateOtherUserResponse(); // Symulacja odpowiedzi innego użytkownika
    }
  };

  const simulateOtherUserResponse = () => {
    setIsTyping(true);
    setTimeout(() => {
      const message = {
        id: Date.now() + 1,
        text: generateRandomResponse(),
        sender: 'other', // odpowiedź od innego użytkownika
      };
      setMessages((prevMessages) => [...prevMessages, message]);
      setIsTyping(false);
    }, 2000); // Opóźnienie odpowiedzi od drugiego użytkownika
  };

  // Generowanie losowej odpowiedzi od innego użytkownika
  const generateRandomResponse = () => {
    const responses = [
      "Zgadzam się z tobą!",
      "Ciekawe, co o tym myślą inni!",
      "Muszę się zastanowić nad tym!",
      "To była dobra decyzja!",
      "Nie jestem pewny, co o tym myśleć...",
      "Jestem w tym samym momencie!",
    ];
    return responses[Math.floor(Math.random() * responses.length)];
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  return (
    <>
      <div className="flex-grow overflow-y-auto space-y-2 p-5">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`p-2 max-w-xs rounded-lg ${
              message.sender === 'me'
                ? 'ml-auto bg-blue-600 text-white'
                : 'mr-auto bg-gray-700 text-white'
            }`}
          >
            <span className="font-semibold">
              {message.sender === 'me' ? 'Ty' : 'Inny użytkownik'}:
            </span>{" "}
            {message.text}
          </div>
        ))}

        {isTyping && (
          <div className="p-2 max-w-xs bg-gray-600 text-white rounded-lg animate-pulse">
            Inny użytkownik pisze...
          </div>
        )}

        <div ref={chatRef} />
      </div>

      <div className="flex flex-col bg-gray-900 p-4 rounded-lg">
        {/* Pole wejściowe i przycisk wysyłania */}
        <div className="mt-4 flex">
          <input
            type="text"
            className="flex-grow p-2 rounded-l-lg bg-gray-800 text-white border border-gray-700 focus:outline-none"
            placeholder="Wpisz swoją wiadomość..."
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyDown={handleKeyPress}
          />
          <button
            onClick={handleSendMessage}
            className="bg-blue-600 text-white p-2 rounded-r-lg hover:bg-blue-700"
          >
            Wyślij
          </button>
        </div>
      </div>
    </>
  );
}

export default Chat;
