import React, { useEffect, useState } from 'react';
import axios from 'axios';
import MarketItemsList from './MarketItemCard';
import MarketAddItem from './FilterBar';
import MarketItemCard from './MarketItemCard';

function MarketPage() {
    const [items, setItems] = useState([]);
    const [filters, setFilters] = useState({});

    useEffect(() => {
        fetchMarketItems();
    }, [filters]);

    const fetchMarketItems = async () => {
        try{
            const {data} = await axios.get('/api/market', {params: filters});
            setItems(data);
        }catch(error){
            console.error('Błąd podczas wydobycia przedmiotów w markecie', error);
        }
    };

    return (
        <div className="p-4 bg-maincolor1 min-h-screen">
        <h1 className="text-3xl font-bold text-center text-maincolor4 mb-6">Market</h1>
        <FilterBar onFilterChange = {setFilters} />
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4">
            {items.map((item) => (
            <   MarketItemCard key={item._id} item={item} />
            ))}
        </div>
    </div>
    );
}

export default MarketPage;
