import React, { useEffect, useState } from 'react';

function Market() {
    const [items, setItems] = useState([]);
    const [filters, setFilters] = useState({ category: '', element: '', sortBy: 'endTime', sortOrder: 'asc', timeLeft: '' });

    useEffect(() => {
        const fetchItems = async () => {
            const queryString = new URLSearchParams(filters).toString();
            const response = await fetch(`http://localhost:8080/api/market?${queryString}`);
            const data = await response.json();
            setItems(data);
        };
        fetchItems();
    }, [filters]);

    return (
        <div className="market">
            {/* Filtry */}
            <div className="filters">
                <select onChange={(e) => setFilters({ ...filters, category: e.target.value })}>
                    <option value="">Category</option>
                    <option value="equipable">Equipable</option>
                    <option value="usable">Usable</option>
                </select>
                {/* Dodaj więcej filtrów */}
            </div>

            {/* Lista przedmiotów */}
            <div className="items">
                {items.map((item) => (
                    <div key={item._id} className="item">
                        <h3>{item.item.name}</h3>
                        <p>{item.type === 'auction' ? `Current Bid: ${item.currentBid || item.startingPrice}` : `Price: ${item.buyoutPrice}`}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Market;
