import React, { useState, useEffect } from "react";
import Modal from "react-modal";

const Market = () => {
    const [activeTab, setActiveTab] = useState("market"); // Zakładki: "market" lub "sell"
    const [marketItems, setMarketItems] = useState([]);
    const [userItems, setUserItems] = useState([]); // Przedmioty użytkownika
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [isBuyModalOpen, setIsBuyModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });

    useEffect(() => {
        fetchMarketItems();
        fetchUserItems();
    }, []);

    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/items", {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów:', response.statusText);
                return;
            }

            const data = await response.json();
            setMarketItems(data);
        } catch (error) {
            console.error('Błąd pobierania przedmiotów:', error);
        }
    };

    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/user/items", {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            });

            if (!response.ok) {
                console.error('Błąd pobierania przedmiotów użytkownika:', response.statusText);
                return;
            }

            const data = await response.json();
            setUserItems(data);
        } catch (error) {
            console.error('Błąd pobierania przedmiotów użytkownika:', error);
        }
    };

    const handleSell = async () => {
        const token = localStorage.getItem('token');
        if (!token || !selectedItem) {
            console.warn('Brak tokenu lub nie wybrano przedmiotu');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/sell", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    ...sellData,
                    itemId: selectedItem._id
                })
            });

            if (!response.ok) {
                console.error('Błąd wystawiania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został wystawiony na sprzedaż!');
            fetchMarketItems();
            setIsSellModalOpen(false);
        } catch (error) {
            console.error('Błąd wystawiania przedmiotu:', error);
        }
    };

    const handleBuy = async (marketItemId) => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch(`http://localhost:8080/api/market/buy`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ marketItemId })
            });

            if (!response.ok) {
                console.error('Błąd kupowania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot został kupiony!');
            fetchMarketItems();
            setIsBuyModalOpen(false);
        } catch (error) {
            console.error('Błąd kupowania przedmiotu:', error);
        }
    };

    return (
        <div className="p-8 bg-maincolor1 text-maincolor4 min-h-screen">
            <div className="flex justify-around mb-8">
                <button onClick={() => setActiveTab("market")} className="text-xl font-bold hover:text-blue-500">Market</button>
                <button onClick={() => setIsSellModalOpen(true)} className="text-xl font-bold hover:text-blue-500">Sell Item</button>
            </div>

            {activeTab === "market" && (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {marketItems.map(item => (
                        <div key={item._id} className="border border-maincolor4 p-6 rounded-lg shadow-lg bg-maincolor2">
                            <h3 className="font-bold text-lg text-maincolor4">{item.item?.name || 'Unnamed Item'}</h3>
                            <p className="text-maincolor3">Type: {item.type === 'fixed' ? 'Sale' : 'Auction'}</p>
                            <p className="text-maincolor3">Price: {item.type === 'fixed' ? item.buyoutPrice : item.currentBid || item.startingPrice}</p>
                            <button 
                                onClick={() => { setSelectedItem(item); setIsBuyModalOpen(true); }} 
                                className="bg-green-500 text-white px-4 py-2 rounded mt-4 hover:bg-green-600"
                            >
                                {item.type === 'fixed' ? 'Buy Now' : 'Bid'}
                            </button>
                        </div>
                    ))}
                </div>
            )}

            <Modal
                isOpen={isSellModalOpen}
                onRequestClose={() => setIsSellModalOpen(false)}
                className="bg-maincolor2 p-8 rounded-lg shadow-xl max-w-lg mx-auto text-maincolor4"
                overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center"
            >
                <h2 className="text-2xl font-bold mb-6">Select an Item to Sell</h2>
                <div className="grid grid-cols-3 gap-4">
                    {userItems.map(item => (
                        <div key={item._id} className="border p-4 rounded-lg bg-maincolor3 text-maincolor1 cursor-pointer" onClick={() => setSelectedItem(item)}>
                            <h3 className="font-bold">{item.name}</h3>
                            <img src={`images/${item.photo}.png`} alt={item.name} className="w-full h-24 object-cover rounded-md" />
                        </div>
                    ))}
                </div>
                {selectedItem && (
                    <div className="mt-4">
                        <h3 className="font-bold mb-2">Selected Item: {selectedItem.name}</h3>
                        <form onSubmit={handleSell}>
                            <div className="mb-4">
                                <label className="block mb-2">Type</label>
                                <select 
                                    name="type" 
                                    value={sellData.type} 
                                    onChange={(e) => setSellData({ ...sellData, type: e.target.value })} 
                                    className="w-full border p-3 rounded bg-maincolor3 text-maincolor1"
                                >
                                    <option value="fixed">Fixed Price</option>
                                    <option value="auction">Auction</option>
                                </select>
                            </div>
                            <div className="mb-4">
                                <label className="block mb-2">Price</label>
                                <input 
                                    type="number" 
                                    name="price" 
                                    value={sellData.price} 
                                    onChange={(e) => setSellData({ ...sellData, price: e.target.value })} 
                                    className="w-full border p-3 rounded bg-maincolor3 text-maincolor1"
                                    required
                                />
                            </div>
                            <div className="mb-4">
                                <label className="block mb-2">Duration (hours)</label>
                                <select 
                                    name="duration" 
                                    value={sellData.duration} 
                                    onChange={(e) => setSellData({ ...sellData, duration: e.target.value })} 
                                    className="w-full border p-3 rounded bg-maincolor3 text-maincolor1"
                                >
                                    <option value="2">2 hours</option>
                                    <option value="8">8 hours</option>
                                    <option value="24">24 hours</option>
                                </select>
                            </div>
                            <button type="submit" className="bg-blue-500 text-white px-6 py-3 rounded hover:bg-blue-600">List Item</button>
                        </form>
                    </div>
                )}
            </Modal>

            <Modal
                isOpen={isBuyModalOpen}
                onRequestClose={() => setIsBuyModalOpen(false)}
                className="bg-maincolor2 p-8 rounded-lg shadow-xl max-w-lg mx-auto text-maincolor4"
                overlayClassName="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center"
            >
                <h2 className="text-2xl font-bold mb-6">Confirm Purchase</h2>
                {selectedItem && (
                    <div>
                        <p className="mb-4"><strong>Item:</strong> {selectedItem.item?.name || 'Unnamed Item'}</p>
                        <p className="mb-4"><strong>Price:</strong> {selectedItem.type === 'fixed' ? selectedItem.buyoutPrice : selectedItem.currentBid || selectedItem.startingPrice}</p>
                        <button 
                            onClick={() => handleBuy(selectedItem._id)} 
                            className="bg-green-500 text-white px-6 py-3 rounded hover:bg-green-600"
                        >
                            Confirm
                        </button>
                    </div>
                )}
            </Modal>
        </div>
    );
};

export default Market;
