import React, {useEffect, useState} from "react";
import MarketItemCard from "./MarketItemCard.js";

function MarketItemsList(){
    const [items,setItems] = useState([]);

    useEffect(() => {
        const fetchMarketItems = async () => {
            try{
                const response = await fetch('http://localhost:8080/api/market/items');
                const data = await response.json();
                setItems(data);
            }catch(error){
                console.error('Błąd podczas pobierania przedmiotów z rynku:', error);
            }
        };
        fetchMarketItems();
    },[]);

    return(
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {items.map((item) => (
                <MarketItemCard key={item._id} item={item} />
            ))}
        </div>
    );
}

export default MarketItemsList;
