import React, { useState, useEffect } from 'react';
import ItemList from './ItemList.js';
import SellModal from './SellModal.js';

const MarketPage = () => {
    const [marketItems, setMarketItems] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [showModal, setShowModal] = useState(false);

    const fetchMarketItems = async () => {
        try {
            const response = await fetch('http://localhost:8080/api/market', {
                method: 'GET',
            });
            if (response.ok) {
                const data = await response.json();
                setMarketItems(data);
            } else {
                console.error('Błąd podczas pobierania przedmiotów z rynku.');
            }
        } catch (error) {
            console.error('Błąd serwera:', error);
        }
    };

    useEffect(() => {
        fetchMarketItems();
    }, []);

    return (
        <div className="p-6">
            <h1 className="text-2xl font-bold mb-4">Rynek</h1>
            <ItemList items={marketItems} />
            <button
                className="mt-4 bg-blue-500 text-white px-4 py-2 rounded shadow"
                onClick={() => setShowModal(true)}
            >
                Wystaw przedmiot
            </button>
            {showModal && (
                <SellModal
                    selectedItem={selectedItem}
                    onClose={() => setShowModal(false)}
                    refreshMarket={fetchMarketItems}
                />
            )}
        </div>
    );
};

export default MarketPage;
