import React, { useState, useEffect } from "react";

const Market = () => {
    const [activeTab, setActiveTab] = useState("shop");
    const [shopItems, setShopItems] = useState([]);
    const [userItems, setUserItems] = useState([]);
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });

    useEffect(() => {
        fetchShopData();
        fetchUserItems();
    }, []);

    const fetchShopData = async () => {
        // Fetch shop items logic
    };

    const fetchUserItems = async () => {
        // Fetch user inventory items logic
    };

    const handleSell = async (type) => {
        if (!selectedItem) return;

        try {
            // Selling item logic
            setIsSellModalOpen(false);
        } catch (error) {
            console.error("Błąd wystawiania przedmiotu:", error);
        }
    };

    return (
        <div className="w-full h-full bg-black flex justify-center p-5 flex-col space-y-4">
            <div className="flex justify-around w-full p-4 bg-gray-800 rounded-xl mb-4">
                <button onClick={() => setActiveTab("shop")} className="text-white">Shop</button>
                <button onClick={() => setActiveTab("inventory")} className="text-white">Inventory</button>
            </div>

            {activeTab === "shop" && (
                <div>
                    {shopItems.map((item) => (
                        <div key={item._id} className="bg-gray-700 p-4 rounded">
                            <h3 className="text-white">{item.name}</h3>
                            <button onClick={() => console.log("Buying", item._id)} className="text-blue-400">Buy</button>
                        </div>
                    ))}
                </div>
            )}

            {activeTab === "inventory" && (
                <div>
                    {userItems.map((item) => (
                        <div key={item._id} className="bg-gray-700 p-4 rounded">
                            <h3 className="text-white">{item.name}</h3>
                            <button onClick={() => { setSelectedItem(item); setIsSellModalOpen(true); }} className="text-green-400">Sell</button>
                        </div>
                    ))}
                </div>
            )}

            {isSellModalOpen && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
                    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white">
                        <h2>Sell Item</h2>
                        {selectedItem && <h3>{selectedItem.name}</h3>}
                        <label>Price:</label>
                        <input type="number" value={sellData.price} onChange={(e) => setSellData({ ...sellData, price: e.target.value })} className="text-black p-2 rounded" />
                        <label>Duration:</label>
                        <select value={sellData.duration} onChange={(e) => setSellData({ ...sellData, duration: e.target.value })} className="text-black p-2 rounded">
                            <option value="2">2 hours</option>
                            <option value="8">8 hours</option>
                            <option value="24">24 hours</option>
                        </select>
                        <div className="flex space-x-4 mt-4">
                            <button onClick={() => handleSell("fixed")} className="bg-blue-500 p-2 rounded">Sell</button>
                            <button onClick={() => handleSell("auction")} className="bg-yellow-500 p-2 rounded">Auction</button>
                            <button onClick={() => setIsSellModalOpen(false)} className="bg-red-500 p-2 rounded">Close</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Market;
