import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

import toast, { Toaster } from 'react-hot-toast';
import { useFormik } from 'formik';
import { registerValidation } from '../helper/validate';
import convertToBase64 from '../helper/convert';
import { registerUser } from '../helper/helper';

import styles from '../styles/Username.module.css';

// Importuj zdjęcia jajek
import egg1 from '../assets/egg1.png';
import egg2 from '../assets/egg2.png';
import egg3 from '../assets/egg1.png';
import egg4 from '../assets/egg2.png';
import egg5 from '../assets/egg1.png';
import egg6 from '../assets/egg2.png';

export default function Register() {
  const navigate = useNavigate();

  const [selectedEgg, setSelectedEgg] = useState(null); // Wybrane jajko
  const [selectedEggBase64, setSelectedEggBase64] = useState(''); // Wybrane jajko w formacie Base64

  const handleEggSelect = async (egg) => {
    setSelectedEgg(egg);

    // Konwertujemy obraz na Base64
    const response = await fetch(egg);
    const blob = await response.blob();
    const base64 = await convertToBase64(blob);
    setSelectedEggBase64(base64); // Ustawiamy Base64 dla wybranego jajka
  };

  const formik = useFormik({
    initialValues: {
      email: '',
      username: '',
      password: '',
    },
    validate: registerValidation,
    validateOnBlur: false,
    validateOnChange: false,
    onSubmit: async (values) => {
      // Dodajemy wybrane jajko w formacie Base64 do danych formularza
      values = { ...values, selectedEgg: selectedEggBase64 || '' };
      let registerPromise = registerUser(values);
      toast.promise(registerPromise, {
        loading: 'Creating...',
        success: <b>Register Successfully...!</b>,
        error: <b>Could not Register.</b>,
      });

      registerPromise.then(function () {
        navigate('/');
      });
    },
  });

  return (
    <div className="container mx-auto h-screen flex flex-col justify-center items-center bg-gray-800">
      <Toaster position="top-center" reverseOrder={false} />

      {/* Nagłówek */}
      <h4 className="text-5xl font-bold text-white mb-9">Wybierz jajko</h4>

      {/* Główna sekcja: wybór jajek i formularz */}
      <div className="flex w-full max-w-5xl justify-center items-center">
        {/* Lewa kolumna: jajka */}
        <div className="flex flex-col items-center w-1/2">
          <div className="eggs grid grid-cols-3 gap-8">
            {[egg1, egg2, egg3, egg4, egg5, egg6].map((egg, index) => (
              <img
                key={index}
                src={egg}
                alt={`Egg ${index + 1}`}
                className={`w-36 h-36 border-4 transition-all duration-300 ${
                  selectedEgg === egg
                    ? 'border-white shadow-lg'
                    : 'border-transparent'
                } hover:scale-110 cursor-pointer`}
                onClick={() => handleEggSelect(egg)} // Ustawiamy wybrane jajko
              />
            ))}
          </div>
        </div>

        {/* Prawa kolumna: formularz */}
        <div className="flex flex-col items-center w-1/2">
          <form className="py-4" onSubmit={formik.handleSubmit}>
            <div className="textbox flex flex-col items-center gap-4">
              <input
                {...formik.getFieldProps('email')}
                className={styles.textbox}
                type="email"
                placeholder="Podaj e-mail*"
              />
              <input
                {...formik.getFieldProps('username')}
                className={styles.textbox}
                type="text"
                placeholder="Podaj nazwę*"
              />
              <input
                {...formik.getFieldProps('password')}
                className={styles.textbox}
                type="password"
                placeholder="Podaj hasło*"
              />
            </div>

            {/* Przyciski */}
            <div className="flex justify-between mt-6">
              <Link
                to="/"
                className="btn bg-gray-500 text-white py-2 px-6 rounded-lg shadow-sm hover:bg-gray-600"
              >
                Powrót
              </Link>
              <button
                type="submit"
                className="btn bg-green-500 text-white py-2 px-6 rounded-lg shadow-sm hover:bg-green-600"
              >
                Dalej
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
