import React, { useEffect, useState } from "react";

function Misions({ data }) {
  const initialTimes = [10, 50, 70, 90, 300];
  const [timeLeftTable, setTimeLeftTable] = useState(initialTimes);

  useEffect(() => {
    const timers = timeLeftTable.map((time, index) => {
      if (time > 0) {
        return setInterval(() => {
          setTimeLeftTable((prev) => {
            const newTimeLeft = [...prev];
            newTimeLeft[index] = Math.max(newTimeLeft[index] - 1, 0); // Uniknięcie ujemnych wartości
            return newTimeLeft;
          });
        }, 1000);
      }
      return null;
    });

    // Wyczyść timery po odmontowaniu komponentu
    return () => timers.forEach((timer) => timer && clearInterval(timer));
  }, []); // Efekt uruchamia się tylko raz przy montowaniu

  // Formatowanie czasu w minutach i sekundach
  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };

  const placeForMissions = Array(6).fill("");
  return (
    <div className="absolute w-full bg-black1 h-screen text-maincolor4">
      {placeForMissions.map((_, index) => (
        <div
          key={index} // Dodanie klucza
          className="w-[55vw] bg-black h-[15vh] m-[1.5vh] rounded-3xl border-2 border-maincolor1 border-[1vh]"
        >
          {initialTimes[index] ?(

          
          <div className="w-full h-full bg-maincolor1 border border-black rounded-3xl border-[1vh] flex items-center justify-between px-4">
            
            <div className="ml-[1vh] mr-[3vh] w-[10vh] h-[10vh] rounded-full">
              <img src="images/water1-3.png" className="rounded-full" alt="Mission" />
            </div>
            <div className="bg-gradient-to-r from-black to-maincolor5 w-4/5 h-2/5 rounded-r-full rounded-l-lg border-maincolor4 border-4 relative">
              <div
                className={`h-full bg-maincolor1 absolute right-0 rounded-r-full `}
                style={{ width: `${(timeLeftTable[index] / initialTimes[index]) * 100 }%` }}
              ></div>
            </div>
            <div className="text-white ml-auto text-[1.8vw] w-[6vh] ml-[3vh]">{formatTime(timeLeftTable[index])}</div>
          </div>
):(<div></div>)}
        </div>
      ))}
      <div className="w-[22vw] h-[50vh] bg-maincolor1 absolute right-0 top-0 m-[1vw] rounded-3xl p-[1vw]">
        <div className="w-full h-full flex grid grid-cols-2 grid-rows-3 gap-[2vw]">
          {placeForMissions.map((_,index) => (
          <div className="w-1/2 h-1/3 grid place-items-center ">
            <div className="relative h-[16vh] aspect-square bg-black rounded-2xl outline outline-4 outline-maincolor4">
              <img src="images/water1-3.png" className="rounded-2xl"></img>
            </div>
          </div>
          )
          )
          }
        </div>
      </div>
      
    </div>
  );
}

export default Misions;
