import React, { useState, useEffect } from "react";

const Market = () => {
    const [activeTab, setActiveTab] = useState("market"); // Domy≈õlnie otwieramy Market
    const [tooltipVisible, setTooltipVisible] = useState(false);
    const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });
    const [tooltipContent, setTooltipContent] = useState(null);
    const [userItems, setUserItems] = useState([]); // Przedmioty u≈ºytkownika
    const [marketItems, setMarketItems] = useState([]); // Przedmioty na markecie
    const [selectedItem, setSelectedItem] = useState(null);
    const [isSellModalOpen, setIsSellModalOpen] = useState(false);
    const [sellData, setSellData] = useState({ type: "fixed", price: "", duration: "2" });

    useEffect(() => {
        fetchMarketItems();
        fetchUserItems();
    }, []);

    /** üî• Poprawione pobieranie przedmiot√≥w z marketu */
    const fetchMarketItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('B≈ÇƒÖd pobierania przedmiot√≥w na markecie:', response.statusText);
                return;
            }

            const data = await response.json();
            console.log("‚úÖ Market items:", data);
            setMarketItems(data);
        } catch (error) {
            console.error('‚ùå B≈ÇƒÖd pobierania przedmiot√≥w na markecie:', error);
        }
    };

    /** üî• Poprawione pobieranie ekwipunku gracza */
    const fetchUserItems = async () => {
        const token = localStorage.getItem('token');
        if (!token) {
            console.warn('Brak tokenu w localStorage');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/usersCreaturesAndItemsData", {
                method: 'GET',
                headers: { 'Authorization': `Bearer ${token}` }
            });

            if (!response.ok) {
                console.error('B≈ÇƒÖd pobierania przedmiot√≥w u≈ºytkownika:', response.statusText);
                return;
            }

            const data = await response.json();
            setUserItems(data?.userItems || []);
        } catch (error) {
            console.error('‚ùå B≈ÇƒÖd pobierania przedmiot√≥w u≈ºytkownika:', error);
        }
    };

    /** üî• Obs≈Çuga sprzeda≈ºy */
    const handleSell = async () => {
        const token = localStorage.getItem('token');
        if (!token || !selectedItem) {
            console.warn('Brak tokenu lub nie wybrano przedmiotu');
            return;
        }

        try {
            const response = await fetch("http://localhost:8080/api/market/sell", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    ...sellData,
                    itemId: selectedItem._id
                })
            });

            if (!response.ok) {
                console.error('B≈ÇƒÖd wystawiania przedmiotu:', response.statusText);
                return;
            }

            alert('Przedmiot zosta≈Ç wystawiony!');
            fetchMarketItems(); // Od≈õwie≈º market
            fetchUserItems(); // Od≈õwie≈º ekwipunek
            setIsSellModalOpen(false);
        } catch (error) {
            console.error('‚ùå B≈ÇƒÖd wystawiania przedmiotu:', error);
        }
    };

    return (
        <div className="w-full h-full bg-black1 flex justify-center p-5 flex-col space-y-4">
            {/* Panel nawigacyjny */}
            <div className="flex justify-around w-full p-4 bg-maincolor1 rounded-xl mb-4 shadow-lg sticky top-0 z-10">
                <button onClick={() => setActiveTab("market")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Market
                </button>
                <button onClick={() => setActiveTab("inventory")} className="text-maincolor4 text-lg hover:text-blue-600 transition-all">
                    Inventory
                </button>
            </div>

            {/* Widok Marketu */}
            {activeTab === "market" && (
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 max-h-[60vh] overflow-y-auto">
                    {marketItems.length === 0 ? (
                        <p className="text-maincolor4 text-center">Brak przedmiot√≥w na rynku.</p>
                    ) : (
                        marketItems.map((item) => (
                            <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all">
                                <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.item.name}</h3>
                                <img src={`images/${item.item.photo}.png`} alt={item.item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                <p className="text-maincolor4 font-bold mt-2">Price: {item.buyoutPrice || item.startingPrice} coins</p>
                            </div>
                        ))
                    )}
                </div>
            )}

            {/* Widok Inventory */}
            {activeTab === "inventory" && (
                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                    {userItems.length === 0 ? (
                        <p className="text-maincolor4 text-center">Tw√≥j inventarz jest pusty!</p>
                    ) : (
                        userItems.map((item) => (
                            <div key={item._id} className="bg-maincolor1 rounded-xl p-4 shadow-xl transform hover:scale-105 transition-all">
                                <h3 className="text-xl text-maincolor4 mb-2 font-bold">{item.name}</h3>
                                <img src={`images/${item.photo}.png`} alt={item.name} className="w-full h-32 object-cover rounded-xl mb-2 border-4 border-maincolor4 shadow-md" />
                                <button onClick={() => { setSelectedItem(item); setIsSellModalOpen(true); }}
                                    className="mt-3 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all">
                                    Sell item
                                </button>
                            </div>
                        ))
                    )}
                </div>
            )}

            {/* Modal sprzeda≈ºy */}
            {isSellModalOpen && selectedItem && (
                <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center">
                    <div className="bg-maincolor1 p-6 rounded-lg shadow-md w-1/4">
                        <h2 className="text-lg font-bold mb-4 text-maincolor4">Sprzedaj przedmiot</h2>
                        <input type="number" value={sellData.price} onChange={(e) => setSellData({ ...sellData, price: e.target.value})} className="w-full p-2 border rounded mb-4 text-black" />
                        <button onClick={handleSell} className="w-full bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 transition-all">Wystaw na sprzeda≈º</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Market;
