import React from 'react';
import { Navigate } from 'react-router-dom';

export const RedirectIfLoggedIn = ({ children }) => {
    const token = localStorage.getItem('token');

    // Jeśli token istnieje, przekieruj użytkownika na stronę główną
    if (token) {
        return <Navigate to="/mainpage" replace={true} />;
    }

    // Jeśli token nie istnieje, renderuj dzieci komponentu (np. logowanie/rejestracja)
    return children;
};
