import React, { useEffect, useState } from "react";
function Misions({data}) {
  const [timeLeft, setTimeLeft] = useState(300);
  const [fullTime, setFullTime] = useState(300);
  useEffect(() => {
    if (timeLeft <= 0) return; // Zatrzymaj odliczanie, gdy czas osiągnie 0

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1); // Zmniejsz czas o 1 co sekundę
    }, 1000);

    return () => clearInterval(timer); // Wyczyść timer po demontażu komponentu
  }, [timeLeft]);

  // Formatowanie czasu w minutach i sekundach
  const formatTime = (time) => {
    const minutes = Math.floor(time / 60);
    const seconds = time % 60;
    return `${minutes}:${seconds < 10 ? `0${seconds}` : seconds}`;
  };
    const placeForMissions = Array(6).fill("");
    return (
      <div className="absolute w-full bg-black1 h-screen  text-maincolor4">
        {placeForMissions.map((_,index) =>(
          <div className="w-[65vw] bg-black h-[15vh] m-[1.5vh]  rounded-3xl border-2 border-maincolor1 border-[1vh]">
              <div className="w-full h-full bg-maincolor1 border border-black rounded-3xl border-[1vh] flex items-center ">
                <div className=" ml-[1vh] w-[10vh] h-[10vh] rounded-full"><img src="images/water1-3.png" className="rounded-full"></img></div>
                <div className="bg-gradient-to-r from-maincolor2 to-maincolor4 w-4/5 h-4/5 rounded-3xl relative">
                <div className='h-full bg-maincolor1 absolute right-0 rounded-3xl'  style={{ width: `${(timeLeft / fullTime) * 100}%` }}></div>
                </div>
                <h1>Odliczanie: {formatTime(timeLeft)}</h1>
              </div>
          </div>
        ))}
      </div>
    );
  }
  
  export default Misions; // Eksport komponentu Content
