import React, { useState, useEffect } from 'react';
import MarketItemCard from './MarketItemCard';
import ItemModal from './ItemModal';

const MarketPage = () => {
    const [items, setItems] = useState([]);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalType, setModalType] = useState('sell'); // 'sell' lub 'auction'
    const token = localStorage.getItem('token');

    const fetchItems = async () => {
        try {
            const response = await fetch('http://localhost:8080/api/market', {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            if (response.ok) {
                const data = await response.json();
                setItems(data);
            } else {
                console.error('Błąd podczas pobierania przedmiotów z rynku.');
            }
        } catch (error) {
            console.error('Błąd serwera:', error);
        }
    };

    useEffect(() => {
        fetchItems();
    }, []);

    const openModal = (type) => {
        setModalType(type);
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div className="p-4">
            <h1 className="text-2xl font-bold mb-4">Market</h1>
            <div className="mb-4">
                <button
                    onClick={() => openModal('sell')}
                    className="bg-green-500 text-white px-4 py-2 rounded mr-2"
                >
                    Wystaw na sprzedaż
                </button>
                <button
                    onClick={() => openModal('auction')}
                    className="bg-blue-500 text-white px-4 py-2 rounded"
                >
                    Wystaw na licytację
                </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {items.map((item) => (
                    <MarketItemCard key={item._id} item={item} />
                ))}
            </div>

            {isModalOpen && <ItemModal type={modalType} closeModal={closeModal} fetchItems={fetchItems} />}
        </div>
    );
};

export default MarketPage;
