import React from 'react';

const ItemList = ({ items }) => {
    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {items.map((item) => (
                <div key={item._id} className="border p-4 rounded shadow">
                    <h2 className="text-xl font-bold">{item.item.name}</h2>
                    <p>Typ: {item.type === 'fixed' ? 'Sprzedaż' : 'Licytacja'}</p>
                    {item.type === 'auction' ? (
                        <p>Obecna oferta: {item.currentBid || item.startingPrice}</p>
                    ) : (
                        <p>Cena: {item.buyoutPrice}</p>
                    )}
                    <p>Sprzedający: {item.seller.username}</p>
                    <p>Do końca: {new Date(item.endTime).toLocaleString()}</p>
                </div>
            ))}
        </div>
    );
};

export default ItemList;
