import React from 'react';
import { useTranslation } from 'react-i18next';
import Chat from './Chat';

// Komponent ogłoszenia
function Notice({ title, content }) {
  return (
    <div className="bg-gray-700 p-4 mb-4 rounded-xl border border-gray-600">
      <h3 className="text-xl font-semibold text-white">{title}</h3>
      <p className="text-white">{content}</p>
    </div>
  );
}

function StartPage() {
  const { t } = useTranslation();

  return (
    <div className="flex h-full">
      {/* Notice Board */}
      <div className="w-2/3 bg-gray-800 p-4">
        <h2 className="text-2xl font-bold text-white mb-4">{t('noticeBoard')}</h2>

        {/* Example Notices */}
        <Notice 
          title={t('importantUpdate')} 
          content={t('updateDetails')}
        />
        <Notice 
          title={t('newEvent')} 
          content={t('eventDetails')}
        />
        <Notice 
          title={t('maintenanceNotice')} 
          content={t('maintenanceDetails')}
        />
      </div>

      {/* Chat Section */}
      <div className="w-1/3 flex flex-col justify-between bg-gray-900 p-4 border-l border-gray-700">
        <h2 className="text-2xl font-bold text-white">{t('chat')}</h2>
        <Chat />
      </div>
    </div>
  );
}

export default StartPage;
